import com.eviware.soapui.model.testsuite.TestCaseRunner;
import com.eviware.soapui.support.UISupport
import com.eviware.soapui.impl.wsdl.actions.testcase.CloneTestCaseAction;
import com.eviware.soapui.model.testsuite.TestCaseRunner;
import java.io.FileReader;
import com.opencsv.CSVReader;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.IOException;
import java.io.*;
import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;
import org.w3c.dom.NamedNodeMap;	

def tcGen
def tcBus
def clr
try{
	testRunner.testCase.testSteps["Remove Execution Driver Instance"].run(testRunner, context)	
	tcGen = testRunner.testCase.testSuite.testCases["Generic Utilities"]
	tcBus = testRunner.testCase.testSuite.testCases["Business Utilities"]
	clr = com.eviware.soapui.SoapUI.getCmdLineRunner()
	def groovyUtils = new  com.eviware.soapui.support.GroovyUtils(context)
	def ProjectPath = groovyUtils.projectPath
	def rootFolderPath = ProjectPath.split("apiautomation")[0]
	def product = context.expand('${#Project#Product}')
	def Project = testRunner.testCase.testSuite.project
	totalThreadCount=context.expand('${#Project#InputThreadCount}')
	tcGen.testSteps["Generate Instructions XML"].run(testRunner, context)
	String fxmlFile = rootFolderPath + "//Test_Instructions//" + product + "//Test_Instructions.xml"
	DocumentBuilderFactory docBuilderFactory = DocumentBuilderFactory.newInstance()
	DocumentBuilder docBuilder = docBuilderFactory.newDocumentBuilder()
	Document doc = docBuilder.parse(fxmlFile)
	NodeList nodeList = doc.getElementsByTagName("TestCase")
	def TotalCaseCount = nodeList.getLength()

	Project.setPropertyValue("TotalCaseCount", TotalCaseCount.toString())
	Project.setPropertyValue("TotalPassCount", "0")
     Project.setPropertyValue("TotalFailCount", "0")
	Project.setPropertyValue("tc_Index", "0")
	def TotalCaseCount1 = context.expand('${#Project#TotalCaseCount}')
	def tc_Index = context.expand('${#Project#tc_Index}')
	def tc_Index_Int =Integer.parseInt(tc_Index)
	if(Integer.parseInt(TotalCaseCount1)<Integer.parseInt(totalThreadCount)){
		Project.setPropertyValue("InputThreadCount", TotalCaseCount1);
	}

	//Setting "Path" property for Logs path
	def todat = new Date()
	def logDate2 = todat.format('dd-MM-yyyy')
	def logDate1 = todat.format('hh-mm-ss a')
	testRunner.testCase.setPropertyValue("logDate1",logDate1)

	def path = ProjectPath + "//LOGS//"+ logDate2 + "//"+ logDate1 + "//"
	File filedirpath = new File(path)
	if(!filedirpath.exists()){
	                filedirpath.mkdirs()
	}

	//Cloning Execution Driver for execution in 'n' parallel Threads
	def totalThreadCount1 = context.expand('${#Project#InputThreadCount}')
	def tc =testRunner.testCase.testSuite.getTestCaseByName( "Execution Driver" )
	for(int i=0;i<Integer.parseInt(totalThreadCount1);i++){
		  def name = "Execution Driver_" + i.toString()
		  def genUtilname="Generic Utilities_" + i.toString()
		  def busUtilname="Business Utilities_" + i.toString()
		  def Utilname="Utilities_" + i.toString()

	       tc.testSuite.cloneTestCase(tc,name)
	       Thread.sleep(500)
	       tc.testSuite.cloneTestCase(tcGen,genUtilname)
	       Thread.sleep(500)
	       tc.testSuite.cloneTestCase(tcBus,busUtilname)
	       testRunner.testCase.testSuite.getTestCaseByName( "Execution Driver_"+i.toString() ).setDisabled(false)
	}

	//Assigning first test case to each cloned instane and trigger the execution
	def a = []
	def b = []
	for(int i=0;i<Integer.parseInt(totalThreadCount1);i++){
		testRunner.testCase.testSuite.getTestCaseByName("Execution Driver_"+i.toString()).setPropertyValue("TestCaseToBeExecuted",tc_Index_Int.toString())
		testRunner.testCase.testSuite.getTestCaseByName("Execution Driver_"+i.toString()).setPropertyValue("Path",path)
		testRunner.testCase.setPropertyValue("Path",path)
	      b[i] =  testRunner.testCase.testSuite.getTestCaseByName("Execution Driver_"+i.toString()).run(null,true)
	      a[i]=b[i].status
	      tc_Index_Int = tc_Index_Int+1
	      Project.setPropertyValue("tc_Index", tc_Index_Int.toString());
	      Thread.sleep(1000); 
	}	
	Thread.sleep(15000);
	//Dynamic assignment of available test case to Ideal Execution driver
      while(tc_Index_Int<Integer.parseInt(TotalCaseCount1)){
		for(int i=0;i<Integer.parseInt(totalThreadCount1);i++){
			a[i]=b[i].status
			if(!(a[i].toString().equals("RUNNING"))){
				log.info a
				testRunner.testCase.testSuite.getTestCaseByName( "Execution Driver_"+i.toString() ).setPropertyValue("testCaseToBeExecuted",tc_Index_Int.toString())
				testRunner.testCase.testSuite.getTestCaseByName( "Execution Driver_"+i.toString() ).setPropertyValue("Path",path)
				b[i] = testRunner.testCase.testSuite.getTestCaseByName( "Execution Driver_"+i.toString()).run(null,true)
				a[i]=b[i].status
				tc_Index_Int=tc_Index_Int+1
				if(tc_Index_Int==Integer.parseInt(TotalCaseCount1-1))
				  break
				Project.setPropertyValue("tc_Index", tc_Index_Int.toString());
				Thread.sleep(3000)
			}
		}
      }
      log.info "Running the last instance of each Execution Driver"

      //Deleting Execution Driver instances while they are in Ideal state

      def total=Integer.parseInt(TotalCaseCount1)+Integer.parseInt(totalThreadCount1)

      while(tc_Index_Int<total){
		for(int i=0;i<Integer.parseInt(totalThreadCount1);i++){
			a[i]=b[i].status
			if(!(a[i].toString().equals("RUNNING"))){
				if(testRunner.testCase.testSuite.getTestCaseByName( "Execution Driver_"+i.toString())!=null){
					Thread.sleep(1000)
					def tc1 =testRunner.testCase.testSuite.getTestCaseByName( "Execution Driver_"+i.toString())
					def tc2=testRunner.testCase.testSuite.getTestCaseByName( "Generic Utilities_"+i.toString())
					def tc3=testRunner.testCase.testSuite.getTestCaseByName( "Business Utilities_"+i.toString())
					tc1.getTestSuite().removeTestCase(tc1)
					tc2.getTestSuite().removeTestCase(tc2)
					tc3.getTestSuite().removeTestCase(tc3)
					tc_Index_Int++
				}
			}
		}	
      }
      log.info "Executed all test cases and deleting all instance of threads"

}catch(Exception e){
	log.error "Unable to execute test scripts, exception occured:-" + e.toString()
	testRunner.fail("ERROR : Unable to proceed Execution, exception occured in Monitor Script")	
}
finally
{

	log.info "Generating HTML Report..."
	tcGen.testSteps["Html Report"].run(testRunner, context)	
	if(!clr)
	{
	//Exporting Script logs in respective folder and clearing log Area
	def logArea = com.eviware.soapui.SoapUI.logMonitor.getLogArea("script log")
	logArea.clear()
	}
	if(testRunner.testCase.getPropertyValue("REPORT_STATUS").equalsIgnoreCase("FAIL"))
	{
		testRunner.fail("ERROR : All executed sripts are not passed")
	}
	tcGen.testSteps["Clear Properties"].run(testRunner, context)
}

def test_suite=testRunner.testCase.testSuite
try
{
      for (test_case  in test_suite.testCaseList)
      {
            if(test_case.name.contains("Execution Driver_") || test_case.name.contains("Generic Utilities_") || test_case.name.contains("Business Utilities_"))
            {
                  testRunner.getTestCase().getTestSuite().removeTestCase(test_case)
            }
      }
}
catch(Exception e)
{
                log.info e.oString()
}

import java.io.*;
import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
