def groovyUtils=new  com.eviware.soapui.support.GroovyUtils(context)
def ProjectPath = groovyUtils.projectPath
def fileName = context.expand('${#TestCase#FileName}')
def inputFile = new File(fileName)
def product = context.expand('${#Project#Product}')
def adapter = context.expand('${#TestCase#Adapter}')
def env = context.expand('${#Project#Environment}')
try {


		def fxmlFile= ProjectPath+'//ENVIRONMENT CONFIGURATION//'+ product+ '//EnvConfig_'+ env +'.xml'
		def tcName = context.expand('${#TestCase#TestCaseName}')
		// Reading environment configuration for Adapter

		log.info '[SYSTEM HEALTH CHECK]: Environment : ' + env
		log.info '[SYSTEM HEALTH CHECK]: Adapter : ' + adapter
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SYSTEM HEALTH CHECK]: Environment : " + env)
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SYSTEM HEALTH CHECK]: Adapter : " + adapter)
		DocumentBuilderFactory docBuilderFactory = DocumentBuilderFactory.newInstance()
		DocumentBuilder docBuilder = docBuilderFactory.newDocumentBuilder()
		Document doc = docBuilder.parse(fxmlFile)

		NodeList node_List = doc.getElementsByTagName("*")

		for(int eTemp = 0;eTemp<node_List.getLength();eTemp++){
			Node eNode = node_List.item(eTemp)
			Element eElement =  eNode
			NamedNodeMap nodeMap = eNode.getAttributes()
			for(int i = 0;i<nodeMap.getLength();i++){
				if(eElement.getAttribute("Adapter").equalsIgnoreCase(adapter)){
					if (eNode.hasChildNodes()) {
						readNode(eNode.getChildNodes(),inputFile)
					}
				}	
			}	
		}
	} catch (Exception e) {
		log.error '[ENVIRONMENT CONFIGURATION]: Environment : ' + e.getMessage();
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[ENVIRONMENT CONFIGURATION]: Environment : " + e.getMessage())
		testRunner.fail("ERROR : Unable to read environment details")
	}
try {
	def FxmlFile= ProjectPath+'//ENVIRONMENT CONFIGURATION//'+ product+ '//Common_Environment_Config_' + env + '.xml'
	DocumentBuilderFactory docBuilderFactory = DocumentBuilderFactory.newInstance()
	DocumentBuilder docBuilder = docBuilderFactory.newDocumentBuilder()
	Document doc = docBuilder.parse(FxmlFile)
	NodeList node_List = doc.getElementsByTagName("*")
	for(int eTemp = 0;eTemp<node_List.getLength();eTemp++){
			Node eNode = node_List.item(eTemp)
			Element eElement =  eNode
			NamedNodeMap nodeMap = eNode.getAttributes()
			for(int i = 0;i<nodeMap.getLength();i++){
				if (eNode.hasChildNodes()) {
						readNode(eNode.getChildNodes(),inputFile)
					}
			}	
		}
	} catch (Exception e) {
		log.error '[ENVIRONMENT CONFIGURATION]: Environment : ' + e.getMessage();
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[ENVIRONMENT CONFIGURATION]: Environment : " + e.getMessage())
		testRunner.fail("ERROR : Unable to read environment details")
		}


//Method to read child nodes of XML	
void readNode(NodeList nodeList, File inputFile) { 
	def project = testRunner.testCase.testSuite.project
		for (int count = 0; count < nodeList.getLength(); count++) {
			Node tempNode = nodeList.item(count)
			// make sure it's element node.
			if (tempNode.getNodeType() == Node.ELEMENT_NODE) {
				// get node name and value
				if(!tempNode.getTextContent().contains("\t")){
					log.info '[SYSTEM HEALTH CHECK]: ' +  tempNode.getNodeName() + " : " + tempNode.getTextContent()
					inputFile.append("\r\n"+(new Date()).toString()+ ":INFO:[SYSTEM HEALTH CHECK]: " +  tempNode.getNodeName() + " : " + tempNode.getTextContent())
					testRunner.testCase.setPropertyValue(tempNode.getNodeName(), tempNode.getTextContent())
				}
				if (tempNode.hasChildNodes()) {
					// loop again if has child nodes
					readNode(tempNode.getChildNodes(),inputFile)

				}

			}
		}	
}

