import java.io.BufferedReader;
import java.io.FileNotFoundException;
import java.io.FileReader;
import java.io.IOException;

def groovyUtils=new  com.eviware.soapui.support.GroovyUtils(context)
def ProjectPath = groovyUtils.projectPath
def rootFolderPath = ProjectPath.split("apiautomation")[0]
def tc=testRunner.testCase
def product = context.expand('${#Project#Product}')
def supplier=context.expand('${#TestCase#ILEC}')
String csvFile = rootFolderPath + "//Test_Data//"+ product+"//Test_Data.csv"
def testCaseName = context.expand('${#TestCase#TestCaseName}')
BufferedReader br = null
String line = ""
String cvsSplitBy = ","
def fileName = context.expand('${#TestCase#FileName}')
def inputFile = new File(fileName)

try {

                  br = new BufferedReader(new FileReader(csvFile))
                  String lineReader ="";
                  Thread.sleep(1000)
                  lineReader = br.readLine()
                  char[] str = lineReader.toCharArray();
                  int colSize=0
                  for(int i=0;i<lineReader.length();i++)
                  {
                      if(str[i]==',')
                      {
                          ++colSize
                      }
                  }
                  String[] cellIterator = lineReader.split(cvsSplitBy)


                  while ((line = br.readLine()) != null)
                  {
                      if(testCaseName.equals(line.split(cvsSplitBy,-1)[0])){
                                      // use comma as separator
                                      String[] temp = line.split(",(?=([^\"]*\"[^\"]*\")*[^\"]*\$)",-1)

       for(int i=1;i<=colSize;i++)
       {
     	   temp[i]=temp[i].toString().replaceAll('\\"',"")
             testRunner.testCase.setPropertyValue(cellIterator[i],temp[i])
       }
                      }
                  }
} catch (Exception e) {
  log.error '[TEST DATA CSV FILE]: Unable to read Test Data CSV file,exception occured:-' + e.toString()
  inputFile.append("\r\n"+(new java.util.Date()).toString()+ ':ERROR:[TEST DATA CSV FILE]: Unable to read Test Data CSV file,exception occured:-' + e.toString())
}

import groovy.sql.Sql
import oracle.jdbc.driver.OracleDriver
import com.eviware.soapui.support.GroovyUtils.*
com.eviware.soapui.support.GroovyUtils.registerJdbcDriver("oracle.jdbc.driver.OracleDriver");
import java.sql.*;
import java.util.regex.Matcher;
import java.util.regex.Pattern;

Connection con
def fileName = context.expand('${#TestCase#FileName}')
def tcName = context.expand('${#TestCase#TestCaseName}')
def inputFile = new File(fileName)
def tc = testRunner.testCase
def tcGen = testRunner.testCase.testSuite.testCases["Generic Utilities_" + tc.name.split("_")[1]]
def tcBus = testRunner.testCase.testSuite.testCases["Business Utilities_" + tc.name.split("_")[1]]

try {
      //Reading DB Properties
      def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
      def SEAPEPdbUrl = context.expand('${#TestCase#SEA_PEP_DB_CONNECTION_URL}')
      def SEAPEPdbDriver = context.expand('${#TestCase#SEA_PEP_DB_DRIVER}')
      def SEAPEPdbUser = context.expand('${#TestCase#SEA_PEP_DB_USERNAME}')
      def SEAPEPdbKey = context.expand('${#TestCase#SEA_PEP_DB_PASSWORD_KEY}')
      def SEAPEPdbEncryptedKey = context.expand('${#TestCase#SEA_PEP_DB_PASSWORD_ENCRYPTED_KEY}')

      def dbUrl = context.expand('${#TestCase#DB_CONNECTION_URL}')
      def dbDriver = context.expand('${#TestCase#DB_DRIVER}')
      def dbUser = context.expand('${#TestCase#DB_USERNAME}')
      def dbKey = context.expand('${#TestCase#DB_PASSWORD_KEY}')
      def dbEncryptedKey = context.expand('${#TestCase#DB_PASSWORD_ENCRYPTED_KEY}')

      def ESRdbUrl = context.expand('${#TestCase#ESR_DB_CONNECTION_URL}')
      def ESRdbDriver = context.expand('${#TestCase#ESR_DB_DRIVER}')
      def ESRdbUser = context.expand('${#TestCase#ESR_DB_USERNAME}')
      def ESRdbKey = context.expand('${#TestCase#ESR_DB_PASSWORD_KEY}')
      def ESRdbEncryptedKey = context.expand('${#TestCase#ESR_DB_PASSWORD_ENCRYPTED_KEY}')

      def MSLVdbUrl = context.expand('${#TestCase#MSLV_DB_CONNECTION_URL}')
      def MSLVdbDriver = context.expand('${#TestCase#MSLV_DB_DRIVER}')
      def MSLVdbUser = context.expand('${#TestCase#MSLV_DB_USERNAME}')
      def MSLVdbKey = context.expand('${#TestCase#MSLV_DB_PASSWORD_KEY}')
      def MSLVdbEncryptedKey = context.expand('${#TestCase#MSLV_DB_PASSWORD_ENCRYPTED_KEY}')

      def npa = context.expand('${#TestCase#NPA}')
      def nxx = context.expand('${#TestCase#NXX}')
      def lineNumber = context.expand('${#TestCase#lineNumber}')
      def eventCodes = context.expand('${#TestCase#EventCodes}')
      def tn
      def ActualColumnData=[]
      def name=[]
      boolean esrFlag = true

      //Decrypting Password
      tcGen.testSteps["Password Decryption"].run(testRunner, context)
      def password = context.PasswordDecryption

      // Connection Setup
      tcGen.testSteps["DB Connection"].run(testRunner, context)
      dbConnection = context.DBConnection
      def updatedPrerequisite = ""
      def DataBase
      ResultSet resultSet
      def preRequisite = context.expand('${#TestCase#PreRequisite}').split("\\|\\|")
      for (int i = 0; i < preRequisite.size(); i++) {
       def DBQueryPreRequisiteDetails = preRequisite[i].split("\\|")
       DataBase = DBQueryPreRequisiteDetails[0]
       switch(DataBase){
         case "SEA_PEP_DB":
                def SEAPEPdbPassword = password.decryptPassword(SEAPEPdbKey, SEAPEPdbEncryptedKey)
                dbConnection.setDBProperties(SEAPEPdbDriver, SEAPEPdbUrl, SEAPEPdbUser, SEAPEPdbPassword)
                con = dbConnection.getDBConnection()
                break;
         case "ADAPTER_DB":
                def dbPassword = password.decryptPassword(dbKey, dbEncryptedKey)
                dbConnection.setDBProperties(dbDriver, dbUrl, dbUser, dbPassword)
                con = dbConnection.getDBConnection()
                break;
         case "ESR_DB":
                def ESRdbPassword = password.decryptPassword(ESRdbKey, ESRdbEncryptedKey)
                dbConnection.setDBProperties(ESRdbDriver, ESRdbUrl, ESRdbUser, ESRdbPassword)
                con = dbConnection.getDBConnection()
                break;
         case "MSLV_DB":
                def MSLVdbPassword = password.decryptPassword(MSLVdbKey, MSLVdbEncryptedKey)
                dbConnection.setDBProperties(MSLVdbDriver, MSLVdbUrl, MSLVdbUser, MSLVdbPassword)
                con = dbConnection.getDBConnection()
                break;
       }

		if(eventCodes.contains(DBQueryPreRequisiteDetails[1]) || (DBQueryPreRequisiteDetails[1].equals("start"))){
		  boolean flag
	       dbQuery = DBQueryPreRequisiteDetails[2]
	       tn = npa + nxx + lineNumber
	       if(dbQuery.contains("hyphen")){
	             tn= npa + "-" +nxx + "-" + lineNumber
	             testRunner.testCase.setPropertyValue("TNwithhyphen",tn) 
	       }
	        Matcher matcher = Pattern.compile("\\\$(\\w+)").matcher(dbQuery); 
	        while (matcher.find()) {
	              String actualStr = matcher.group(1);
	              dbQuery = dbQuery.replace("\$"+actualStr,"'"+testRunner.testCase.getPropertyValue(actualStr)+"'");
	        }
	       log.info '[PREREQUISITE STEP]: Query to be executed in : ' + DataBase + ' and Query is : ' + dbQuery
	       inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query to be executed in : " + DataBase + " and Query is : " + dbQuery)
	       resultSet = dbConnection.getResultSet(con,dbQuery)
	       colSize = 0
	       int count =0;

	       if(dbQuery.startsWith("SELECT")){
              while (resultSet.next()){
              	flag=true
	              rsmd = resultSet.getMetaData()
	              colSize=rsmd.getColumnCount()
	              for(int col=0;col<colSize;col++)
	                 {
	                   ActualColumnData[col] = resultSet.getString(col+1)          
	                   name[col] = rsmd.getColumnName(col+1)                                                          
	                   tc.setPropertyValue(name[col],ActualColumnData[col])
	                   log.info '[PREREQUISITE STEP]: Query Executed Successfully in  ' + DataBase + ' DB' + ' and result is :' + ActualColumnData[col]
	                   inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query Executed Successfully in  " + DataBase + "  DB and result is  : " + ActualColumnData[col])

	                   if((context.expand('${#TestCase#Adapter}').equalsIgnoreCase("GENERIC ASR")) && (name[col].equalsIgnoreCase("URL"))){
	                   	 count++;
	                   	 log.info '[PREREQUISITE STEP]: Query Executed Successfully in  ' + DataBase + ' DB and Count of '+ name[col] + 'is :' + count
	                     inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query Executed Successfully in  " + DataBase + "  DB and Count of "+ name[col] +"is  : " +  count)	                   	 	                  
	                  	} 
	                  } 	                  
	                 }              	                      

			if(colSize == 0 && (DBQueryPreRequisiteDetails.size() == 5 || DBQueryPreRequisiteDetails.size() == 4) && DBQueryPreRequisiteDetails[3].startsWith("UPDATE")){
				dbQuery = DBQueryPreRequisiteDetails[3]
              		matcher = Pattern.compile("\\\$(\\w+)").matcher(dbQuery); 
			        while (matcher.find()) {
			              String actualStr = matcher.group(1);
			              dbQuery = dbQuery.replace("\$"+actualStr,"'"+testRunner.testCase.getPropertyValue(actualStr)+"'");
	        			}
			    int action = con.prepareStatement(dbQuery).executeUpdate();
			    log.info '[PREREQUISITE STEP]: Query to be executed in : ' + DataBase + ' and Query is : ' + dbQuery
			    inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query to be executed in : " + DataBase + " and Query is : " + dbQuery)
	              if(action == 0 && DBQueryPreRequisiteDetails.size() == 5){
              		dbQuery = DBQueryPreRequisiteDetails[4]
              		matcher = Pattern.compile("\\\$(\\w+)").matcher(dbQuery); 
			        while (matcher.find()) {
			              String actualStr = matcher.group(1);
			              dbQuery = dbQuery.replace("\$"+actualStr,"'"+testRunner.testCase.getPropertyValue(actualStr)+"'");
	        			}
				con.prepareStatement(dbQuery).executeUpdate()
				log.info '[PREREQUISITE STEP]: Query to be executed in : ' + DataBase + ' and Query is : ' + dbQuery
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query to be executed in : " + DataBase + " and Query is : " + dbQuery)
	              }
	             con.setAutoCommit(false)          
	             con.commit()
	             tcBus.testSteps["Tier2 Service"].run(testRunner, context)
	             flag=true
			}
			if(colSize==0 && DBQueryPreRequisiteDetails.size() == 4 && DBQueryPreRequisiteDetails[3].startsWith("DELETE")){
				flag=true
				log.info '[PREREQUISITE STEP]: Query Executed Successfully in : ' + DataBase + ' No Result found in DB'
            	 	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query Executed Successfully in : " + DataBase + " No Result found in DB")	     
			}
			if(colSize!=0 && DBQueryPreRequisiteDetails.size() == 4 && DBQueryPreRequisiteDetails[3].startsWith("DELETE")){
				dbQuery = DBQueryPreRequisiteDetails[3]
              		matcher = Pattern.compile("\\\$(\\w+)").matcher(dbQuery); 
			        while (matcher.find()) {
			              String actualStr = matcher.group(1);
			              dbQuery = dbQuery.replace("\$"+actualStr,"'"+testRunner.testCase.getPropertyValue(actualStr)+"'");
	        			}
			    int action = con.prepareStatement(dbQuery).executeUpdate();
			    log.info '[PREREQUISITE STEP]: Query to be executed in : ' + DataBase + ' and Query is : ' + dbQuery
			    inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query to be executed in : " + DataBase + " and Query is : " + dbQuery)
	             con.setAutoCommit(false)          
	             con.commit()
	             tcBus.testSteps["Tier2 Service"].run(testRunner, context)
	             flag=true
			}
            if(!flag){
            	 log.info '[PREREQUISITE STEP]: Query Executed Successfully in : ' + DataBase + ' No Result found in DB'
            	 inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query Executed Successfully in : " + DataBase + " No Result found in DB")	     
	         	}

	       }	         	       
	       else{
	       	int action = con.prepareStatement(dbQuery).executeUpdate();
	              if(action == 0 && DBQueryPreRequisiteDetails.size() == 4){
	         		dbQuery = DBQueryPreRequisiteDetails[3]
	         		matcher = Pattern.compile("\\\$(\\w+)").matcher(dbQuery); 
			        while (matcher.find()) {
			              String actualStr = matcher.group(1);
			              dbQuery = dbQuery.replace("\$"+actualStr,"'"+testRunner.testCase.getPropertyValue(actualStr)+"'");
	        			}
				con.prepareStatement(dbQuery).executeUpdate()
				log.info '[PREREQUISITE STEP]: Query to be executed in : ' + DataBase + ' and Query is : ' + dbQuery
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query to be executed in : " + DataBase + " and Query is : " + dbQuery)
	              }
	             con.setAutoCommit(false)          
	             con.commit()
	             tcBus.testSteps["Tier2 Service"].run(testRunner, context)
	             flag=true
	             log.info '[PREREQUISITE STEP]: Query Executed Successfully in : ' + DataBase + ' DB'
	             inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: Query Executed Successfully in : " + DataBase + " DB")
	             con.close()
	          }
	         preRequisite[i]=DBQueryPreRequisiteDetails[0]+"|#|"+DBQueryPreRequisiteDetails[2]
		     if(!flag){
				status = "FAIL"
				log.error '[PREREQUISITE STEP]: ' + status	
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[PREREQUISITE STEP]: " + status)
			}
			else{
				status = "PASS"
				log.info '[PREREQUISITE STEP]: ' + status	
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE STEP]: " + status)	
			}
			tc.setPropertyValue("preValFlag",flag.toString())
	      }
      updatedPrerequisite = updatedPrerequisite + preRequisite[i] +"||"
   }
   updatedPrerequisite=updatedPrerequisite.substring(0, updatedPrerequisite.length()-2);
   testRunner.testCase.setPropertyValue("PreRequisite",updatedPrerequisite)
}catch(Exception e){
	 log.error "[PREREQUISITE STEP]: Unable to perform DB Validation - " + e.getMessage()
	 inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[PREREQUISITE STEP]: Unable to perform DB Validation - " + e.getMessage())
}finally {
	con.close()
}

