				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[PREREQUISITE DATA SETUP]:CCNA:- " + CCNA[j] + "------------------------")
          		if(nx.toString().equals("null"))
          		{

          			log.info '[PREREQUISITE DATA SETUP]:NPA_NXX--------' + NPA_NXX2[j] + "------------------------"
