					}

					//tc.setPropertyValue("NPA_NXX",NPA_NXX[j])
					//tc.setPropertyValue("LINE_FR",LINE_FR[j])
					//tc.setPropertyValue("LINE_TO",LINE_TO[j])
					//tc.setPropertyValue("LRN",LRN[j])
          			break
          		}
          	}
          }
		tc.setPropertyValue("preReqFlag",flag.toString())
     }catch(Exception e){
		log.error "[PREREQUISITE DATA SETUP]: " + "Exception-->"+e
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[PREREQUISITE DATA SETUP]: " + "Exception-->"+e)
		//e.printStackTrace();
	}  
}catch(Exception e){
	log.error '[PREREQUISITE DATA SETUP]: ' + e.getMessage()	
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[PREREQUISITE DATA SETUP]: " + e.getMessage())
}

