void GenerateTNhyphen(String npa_nxx, String lineNumber ){
      def tn= npa_nxx + "-" + lineNumber
      testRunner.testCase.setPropertyValue("TNwithHyphen",tn)
}

void GenerateTNwithouthyphen(npa_nxx, lineNumber ){
      def temp= []
      temp = npa_nxx.split("-")
      def tn=temp[0]+temp[1]+ lineNumber.toString()
      testRunner.testCase.setPropertyValue("TN",tn)
}

void GenerateRandomNumericProperties(String propName,int size){
      value = org.apache.commons.lang.RandomStringUtils.randomNumeric(size)
      testRunner.testCase.setPropertyValue(propName,value)
} 

void GenerateRandomAlphaNumericProperties(String propName, int size){
      String SALTCHARS = "ABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890";
      String value = ""
      Random rnd = new Random();
      while (value.length() < size) { // length of the random string.
            int index = (int) (rnd.nextFloat() * SALTCHARS.length());
            value = value + SALTCHARS.charAt(index);
      }
      testRunner.testCase.setPropertyValue(propName,value)
}

void GenerateFutureDate_YYYYMMDD(String propName,int days){
      SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd")
      Calendar c = Calendar.getInstance()
      c.setTime(new Date())
      c.add(Calendar.DATE, days) 
      String value = sdf.format(c.getTime())
      testRunner.testCase.setPropertyValue(propName,value)           
}
void GenerateFutureDate_YYYYMMDDHHMMSSA(String propName,int days){
      SimpleDateFormat sdf = new SimpleDateFormat("MM-dd-yyyy-hhmmssa")
      Calendar c = Calendar.getInstance()
      c.setTime(new Date())
      c.add(Calendar.DATE, days) 
      String value = sdf.format(c.getTime())
      testRunner.testCase.setPropertyValue(propName,value)           
}
void GenerateSpecificDateTime_LRN(String propName,String nowFormat){
      SimpleDateFormat sdf = new SimpleDateFormat("MM-dd-yyyy-hhmmssa")
      Calendar c = Calendar.getInstance()
      c.setTime(new Date())
      String value = sdf.format(c.getTime())
      testRunner.testCase.setPropertyValue(propName,value+nowFormat) 
}


