 		propName=propName.substring(0,2)	 
	} 
      value = org.apache.commons.lang.RandomStringUtils.randomNumeric(8)
      testRunner.testCase.setPropertyValue("PONRandomNumeric",value.toString())
      testRunner.testCase.setPropertyValue("PON",propName+"AUTO"+value)
}

