import com.eviware.soapui.support.XmlHolder;

def groovyUtils=new  com.eviware.soapui.support.GroovyUtils(context)
def product = context.expand('${#Project#Product}')
def adapter = context.expand('${#TestCase#Adapter}')
def tc_Name = context.expand('${#TestCase#TestCaseName}')
def customer = context.expand('${#TestCase#Customer}')
def tc = testRunner.testCase;
def tcBus = testRunner.testCase.testSuite.testCases["Business Utilities_" + tc.name.split("_")[1]]
def project = tc.testSuite.project
def fileName = context.expand('${#TestCase#FileName}')
def inputFile = new File(fileName)
try{
	String supValue=context.expand('${#TestCase#SupFlow}').split("-")[1].split("P")[1]      
	def test_Suite = product + "_REQUEST_TEMPLATES"
	def test_Case = adapter + "_REQUEST_TEMPLATES"
	def test_Step = context.expand('${#TestCase#InputXmlType}')
	def inputXmlType = context.expand('${#TestCase#InputXmlType}')
	def lineNumber = context.expand('${#TestCase#lineNumber}')
	def ver = Integer.parseInt(context.expand('${#TestCase#Ver}'))
     def tn = context.expand('${#TestCase#TN}')
     def npa_nxx = context.expand('${#TestCase#NPA_NXX}')
     def Invalid_NPA_NXX = context.expand('${#TestCase#Invalid_NPA_NXX}')
     def autoactivation = context.expand('${#TestCase#AutoActivation}')
     def autoactivationsup = context.expand('${#TestCase#AutoActivationSUP}')
	def onspid= context.expand('${#TestCase#ONSPID}')

 	tc.setPropertyValue("AutoActivation",autoactivationsup)
	def correlationIDInt = Long.parseLong(context.expand('${#TestCase#correlationIDInt}'))
	correlationIDInt = correlationIDInt + 1
	tc.setPropertyValue("CORRELATIONID",onspid+correlationIDInt.toString()) 
	tc.setPropertyValue("correlationIDInt",correlationIDInt.toString()) 

      if (adapter.equalsIgnoreCase("GENERIC WIRELESS") || adapter.equalsIgnoreCase("METRO PCS")) {
                                test_Step = test_Step + "-SUP" + supValue 
                }
     if(!Invalid_NPA_NXX.equals("")) {
           tn = npa_nxx + lineNumber
           tn = tn.replaceAll("-","")
           tc.setPropertyValue("TN",tn)
     } else {
           tn = tn.replaceAll("-","")
           tc.setPropertyValue("TN",tn)               
     }
     tc.setPropertyValue("Event","supplement")
     tc.setPropertyValue("SUP",supValue)
     if(supValue=="3" || supValue=="9"){
	       tc.setPropertyValue("LSRRemark","Test Remarks")
     }
	if(supValue=="1"||supValue=="3" || supValue=="9"){
		ver ++
		tc.setPropertyValue("VER","0" + ver.toString())	
	}
	if(supValue=="2"){
		ver ++
		tc.setPropertyValue("VER","0" + ver.toString())
		tc.setPropertyValue("DDD",tc.getPropertyValue("new_DDD"))                
	}
	if(test_Step.equalsIgnoreCase("Portin_Voipport") && customer.equalsIgnoreCase("TWC") && supValue=="3"){
		tc.setPropertyValue("TOS","1AFN")
		tc.setPropertyValue("TOA","B")
	}
	if((supValue=="9")&&(inputXmlType.equalsIgnoreCase("Portin_Voipport") && customer.equalsIgnoreCase("TWC"))){
	  tc.setPropertyValue("SUP","3")            
	}

	log.info '[SUPPLEMENT '+ supValue + ' REQUEST ENVELOPE]: TEST CASE:-' + tc_Name
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SUPPLEMENT "+ supValue + " REQUEST ENVELOPE]: TEST CASE:-" + tc_Name)
	log.info '[SUPPLEMENT '+ supValue + ' REQUEST ENVELOPE]: TEMPLATE NAME:-' + test_Step
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SUPPLEMENT "+ supValue + " REQUEST ENVELOPE]: TEMPLATE NAME:-" + test_Step)
	def req_XML;

	if(test_Step.contains("Commercial") || test_Step.contains("MultiTN") || test_Step.contains("MTN")){
		tcBus.testSteps["Repeating Nodes"].run(testRunner, context)
		req_XML=tc.getPropertyValue("Request") 
	}
	else{
		req_XML = project.getTestSuiteByName(test_Suite).getTestCaseByName(test_Case).getTestStepByName(test_Step).getPropertyValue("Request")
	}
	if(context.expand('${#TestCase#NIValidation}').contains("SUP")){
	tc.setPropertyValue("NIFlag","true")
	}
	else{
	tc.setPropertyValue("NIFlag","false")
	}
	//Find parameteres using Regular Expression
	Pattern regex = Pattern.compile("(>\\\$((.*))<)")
	Matcher matcher = regex.matcher(req_XML)
	def prop_Key = []
	def j = 0;
	def prop_Value = []
	req_XML.replaceAll(">\r",">")
	log.info '[SUPPLEMENT '+ supValue + ' REQUEST ENVELOPE]: NODES TO REPLACE IN REQUEST ENVELOPE-'
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SUPPLEMENT "+ supValue + " REQUEST ENVELOPE]: NODES TO REPLACE IN REQUEST ENVELOPE-")
	// Declare Hashmap to store Test Data
	Map<String,String> nodes_ToReplace = new HashMap<String,String>()
	while(matcher.find()){
		for (int i = 0; i < matcher.groupCount(); i++) 
		{
			if(i == 1||i==3)
			{
				nodes_ToReplace.put(matcher.group(1).substring(1, matcher.group(1).length() - 1) , matcher.group(3).substring(1, matcher.group(3).length() - 1))
			}
		}
	}

	Set<String> keys = nodes_ToReplace.keySet()
	InputStream XmlParse  = new ByteArrayInputStream(req_XML.getBytes("UTF-8"));
	DocumentBuilderFactory docBuilderFactory = DocumentBuilderFactory.newInstance()
	DocumentBuilder docBuilder = docBuilderFactory.newDocumentBuilder()
	Document document = docBuilder.parse(XmlParse)
	NodeList nodeList = document.getElementsByTagName("*")
	def node_Names = []
	def iterator = 1

	for (int etemp = 0; etemp < nodeList.getLength(); etemp++)
	{
		Node eNode = nodeList.item(etemp);
		if (eNode.getNodeType() == Node.ELEMENT_NODE) 
		{
			node_Names [etemp] = eNode.getNodeName()
			for(String key: keys){
				if(key.equalsIgnoreCase(eNode.getTextContent())){
					log.info '[SUPPLEMENT '+ supValue + ' REQUEST ENVELOPE]: ' + iterator + ". " + nodes_ToReplace.get(key) + " = " + tc.getPropertyValue(nodes_ToReplace.get(key))
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SUPPLEMENT "+ supValue + " REQUEST ENVELOPE]: " + iterator + ". " + nodes_ToReplace.get(key) + " = " + tc.getPropertyValue(nodes_ToReplace.get(key)))
					eNode.setTextContent(tc.getPropertyValue(nodes_ToReplace.get(key)))
					iterator ++
				}
			}
		}
		for (j = 0; j < nodeList.item(etemp).getAttributes().getLength();j++){
			if (nodeList.item(etemp).getAttributes().getLength() > 0 && nodeList.item(etemp).getAttributes().item(j).getNodeValue().startsWith("\${")){
				Node value = nodeList.item(etemp).getAttributes().item(j);
				def attrValue = value.getNodeValue();
				def valueToReplace = attrValue.substring(2, attrValue.length() - 1);
				log.info '[SUPPLEMENT '+ supValue + ' REQUEST ENVELOPE]: ' + iterator + ". " + valueToReplace + " = " + tc.getPropertyValue(valueToReplace)
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SUPPLEMENT "+ supValue + " REQUEST ENVELOPE]: " + iterator + ". " + valueToReplace + " = " + tc.getPropertyValue(valueToReplace))
				value.setNodeValue(tc.getPropertyValue(valueToReplace));
				iterator ++
			}
		}
	}

	TransformerFactory transformerFactory = TransformerFactory.newInstance()
	Transformer transformer = transformerFactory.newTransformer()
	StringWriter writer = new StringWriter()
	DOMSource source = new DOMSource(document)
	StreamResult result = new StreamResult(writer)
	transformer.transform(source, result)
	def output = writer.getBuffer().toString()
	if(adapter.equalsIgnoreCase("TWC") || adapter.equalsIgnoreCase("AN")){
		output = output.replaceAll(' xmlns=""', "").replaceAll("(<\\?[^<]*\\?>)?", "")
	}
	else if(adapter.equalsIgnoreCase("CHARTER")){
		output = output.replaceAll("(<\\?[^<]*\\?>)?", "")         
	}
	else if (adapter.equalsIgnoreCase("GENERIC WIRELESS")||adapter.equalsIgnoreCase("COMMERCIAL")||adapter.equalsIgnoreCase("GENERIC WIRELINE")||adapter.equalsIgnoreCase("COMCAST DL")||adapter.equalsIgnoreCase("METRO PCS")||adapter.equalsIgnoreCase("AN")){
		output = output.replaceAll('xmlns=""', "") //remove blank xmlns declaration 
	}
	tc.setPropertyValue("Request",output)
	tc.setPropertyValue("RequestXml",output)
	String replaceValue= context.expand('${#TestCase#ReplaceTagValueInXml}')
	if(replaceValue != ""){
		replaceValue = replaceValue.split("-")[1]
 	}
	if(replaceValue.contains("SUP")){
		tcBus.testSteps["ReplaceTagValue In Payload"].run(testRunner, context)
		output = tc.getPropertyValue("Request"); 
	} 
	tc.setPropertyValue("RequestXml",output)
	String tagValue= context.expand('${#TestCase#RemoveTagValueFromAdapterXml}')
	if(tagValue != ""){
		tagValue = tagValue.split("-")[1]	
	}
	String tagName= context.expand('${#TestCase#RemoveTagNameFromAdapterXml}')
	if(tagName != ""){
		tagName = tagName.split("-")[1]	
	}	
	if(tagValue.contains("SUP") || tagName.contains("SUP")){
		tc.setPropertyValue("Request",output)
		tcBus.testSteps["Remove Tag from Payload"].run(testRunner, context)
		output = tc.getPropertyValue("Request"); 
	}
	tc.setPropertyValue("RequestXml",output)
	log.info '[SUPPLEMENT '+ supValue + ' REQUEST ENVELOPE]:- Final Payload:-' +output
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SUPPLEMENT "+ supValue + " REQUEST ENVELOPE]:- Final Payload:-" +output)
	testRunner.testCase.setPropertyValue("TestStepToBeVerify", "SUPPLEMENT "+ supValue)
}catch(Exception e){
	log.error "[SUPPLEMENT REQUEST ENVELOPE]:- Unable to generate request XML - " + e.getMessage()
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[SUPPLEMENT REQUEST ENVELOPE]:- Unable to generate request XML - " + e.getMessage())
	testRunner.testCase.setPropertyValue("TestStepToBeVerify", "SUPPLEMENT "+ supValue)
}

