def testStepToBeVerify = context.expand('${#TestCase#TestStepToBeVerify}')
def serviceType = context.expand('${#TestCase#SERVICE_TYPE}')
def fileName = context.expand('${#TestCase#FileName}')
def inputFile = new File(fileName)
def interfaceStepName = context.expand('${#TestCase#InterfaceStepName}')
if(adp.equalsIgnoreCase("CHARTER")||adp.equalsIgnoreCase("SDL")){
		serviceType = adp
}
if(adp.equalsIgnoreCase("Commercial")){
		serviceType = "RPC"
}
if(adp.equalsIgnoreCase("GENERIC ASR")){
		serviceType = "ASR"
}
if(adp.equalsIgnoreCase("UNIVERSAL LRN CHANGE")){
		serviceType = "LRN"
}
if(negative_Identifier == "") {
	negative_Identifier="N";
}
if(context.expand('${#TestCase#NIFlag}').equalsIgnoreCase("true") && negative_Identifier == "Y"){
	negative_Identifier="Y";
}
else{
	negative_Identifier="N";
} 
interfaceStepName =  "Submit Request - " + serviceType
def assertionsList = testRunner.testCase.getTestStepByName(interfaceStepName).getAssertionList()
def RES= testRunner.testCase.getTestStepByName(interfaceStepName).getPropertyValue("Response")
def req2 = testRunner.testCase.getTestStepByName(interfaceStepName).testRequest.response.getResponseHeaders()

flag = false
message = ""
if(adp.equalsIgnoreCase("GENERIC ASR") && !req2["#status#"].toString().equalsIgnoreCase("[HTTP/1.1 200 ]")){
	log.info '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: RESPONSE RECEIVED -' + RES
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:["+ testStepToBeVerify + " RESPONSE VALIDATION]: RESPONSE RECEIVED -" +RES)

	if(!req2["#status#"].toString().equalsIgnoreCase("[HTTP/1.1 200 ]")){
	for( e in assertionsList){
			if(e.getName() == "Contains" || e.getName() == "Not Contains")
			{
				message = e.getName() + " : CONTENT - " + e.getToken() + " : " + e.getStatus()
			}
			else  if(e.getName() == "Invalid HTTP Status Codes")
			{
				message = e.getName() + " : CONTENT - " + " : " + req2["#status#"].toString()

			}
			else
			{
				message = e.getName() + " : " +e.getStatus()
			}

			log.info '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: ASSERTION MESSAGES - ' + message	
			inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[" + testStepToBeVerify +" RESPONSE VALIDATION]: ASSERTION MESSAGES -" + message)
			errors =  e.getErrors()
			if(!e.getStatus().toString().trim().equals("VALID"))
			{
				flag = true
				if(negative_Identifier == "N"){
					log.error '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: ASSERTION STATUS IS - ' + e.getStatus()
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" + testStepToBeVerify +" RESPONSE VALIDATION]: ASSERTION STATUS IS -" + e.getStatus())
					break;
				}
				if(negative_Identifier == "Y"){
					log.info '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: ASSERTION STATUS IS - ' + e.getStatus()
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[" + testStepToBeVerify +" RESPONSE VALIDATION]: ASSERTION STATUS IS -" + e.getStatus())
					break;
				}
			}
			if(e.getErrors() != null)
			{
				flag = true
				log.error '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: ASSERTION ERRORS - ' + errors[0].getMessage()
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" + testStepToBeVerify +" RESPONSE VALIDATION]: ASSERTION ERRORS - " + errors[0].getMessage())	
				break;
			}
	}
	}

else{
	flag = true
	log.error '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: STATUS FOR RECEIVED RESPONSE -' + req2["#status#"].toString()
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" + testStepToBeVerify +" RESPONSE VALIDATION]: STATUS FOR RECEIVED RESPONSE -" + req2["#status#"].toString())

}

}
else{
if(req2["#status#"].toString().equalsIgnoreCase("[HTTP/1.1 200 OK]") || req2["#status#"].toString().equalsIgnoreCase("[HTTP/1.1 200 ]")){
	log.info '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: RESPONSE RECEIVED -' + RES
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:["+ testStepToBeVerify + " RESPONSE VALIDATION]: RESPONSE RECEIVED -" +RES)

	for( e in assertionsList){
			if(e.getName() == "Contains" || e.getName() == "Not Contains")
			{
				message = e.getName() + " : CONTENT - " + e.getToken() + " : " + e.getStatus()
			}
			else  if(e.getName() == "Valid HTTP Status Codes")
			{
				message = e.getName() + " : CONTENT - " + " : " + req2["#status#"].toString()

			}
			else
			{
				message = e.getName() + " : " +e.getStatus()
			}

			log.info '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: ASSERTION MESSAGES - ' + message	
			inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[" + testStepToBeVerify +" RESPONSE VALIDATION]: ASSERTION MESSAGES -" + message)
			errors =  e.getErrors()
			if(!e.getStatus().toString().trim().equals("VALID"))
			{
				flag = true
				if(negative_Identifier == "N"){
					log.error '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: ASSERTION STATUS IS - ' + e.getStatus()
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" + testStepToBeVerify +" RESPONSE VALIDATION]: ASSERTION STATUS IS -" + e.getStatus())
					break;
				}
				if(negative_Identifier == "Y"){
					log.info '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: ASSERTION STATUS IS - ' + e.getStatus()
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[" + testStepToBeVerify +" RESPONSE VALIDATION]: ASSERTION STATUS IS -" + e.getStatus())
					break;
				}
			}
			if(e.getErrors() != null)
			{
				flag = true
				log.error '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: ASSERTION ERRORS - ' + errors[0].getMessage()
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" + testStepToBeVerify +" RESPONSE VALIDATION]: ASSERTION ERRORS - " + errors[0].getMessage())	
				break;
			}
	}

}
else{
	flag = true
	log.error '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: STATUS FOR RECEIVED RESPONSE -' + req2["#status#"].toString()
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" + testStepToBeVerify +" RESPONSE VALIDATION]: STATUS FOR RECEIVED RESPONSE -" + req2["#status#"].toString())

}
}
testRunner.testCase.setPropertyValue("ResponseVerifyStatus", flag.toString())
if(flag && negative_Identifier == "N"){
	status = "FAIL"
	log.error '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: FINAL RESPONSE STATUS - ' + status	
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" + testStepToBeVerify +" RESPONSE VALIDATION]: FINAL RESPONSE STATUS - " + status)
}
else if(flag && negative_Identifier == "Y"){
	status = "PASS"
	log.info '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: FINAL RESPONSE STATUS - ' + status	
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[" + testStepToBeVerify +" RESPONSE VALIDATION]: FINAL RESPONSE STATUS - " + status)	
}
else if(!flag && negative_Identifier == "Y"){
	status = "FAIL"
	log.error '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: FINAL RESPONSE STATUS - ' + status	
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" + testStepToBeVerify +" RESPONSE VALIDATION]: FINAL RESPONSE STATUS - " + status)	
}
else if(!flag && negative_Identifier == "N"){
	status = "PASS"
	log.info '['+ testStepToBeVerify + ' RESPONSE VALIDATION]: FINAL RESPONSE STATUS - ' + status	
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[" + testStepToBeVerify +" RESPONSE VALIDATION]: FINAL RESPONSE STATUS - " + status)	
}

