	dbQuery = "Select * from SEA_TXEVENT_HISTORY a, sea_esr_trans b where a.trans_parent_oid=b.oid and b.oid in (select OID from SEA_ESR_TRANS where PON='" + context.expand('${#TestCase#PON}') + "' and VER='" + context.expand('${#TestCase#VER}') + "') order by a.parent_oid, a.oid"
	log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: Validation of Event Codes, query executed in SEA_TXEVENT_HISTORY & SEA_ESR_TRANS:-' + dbQuery
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: Validation of Event Codes, query executed in SEA_TXEVENT_HISTORY & SEA_ESR_TRANS:-" + dbQuery)

	//Declaring required variables
	def actualColumnValue = []
	int iterator
	boolean dbFlag = false

	def trans_OID
	def totaltime = 0
	def orderSubType = context.expand('${#TestCase#OrderSubType}')
	def timeOut = Integer.parseInt(context.expand('${#Project#DBExplicitTimeOut}'))
	def expColValuesSize = ExpectedColumnValues.size()
	def desiredValue = null
	def req_Dt
	def res_Dt
	def status
	def seaDiscoActualValues = []
	ResultSet resultSet
	tcGen.testSteps["Delay"].run(testRunner, context)
	log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: Expected Result: Column Name: EVENT CODE & Expected Values: ' + ExpectedColumnValues
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: Expected Result: Column Name: EVENT CODE & Expected Values: " + ExpectedColumnValues)
	//Executing Query in Explicit Wait with maximum window of 35 seconds
	for(int l = 0; l < expColValuesSize; l++){
		while (!actualColumnValue.contains(ExpectedColumnValues[l])){
		//Static Delay
		def rejectFailflag=true
		iterator = 0
		resultSet = dbConnection.getResultSet(con, dbQuery)
		if (resultSet.isBeforeFirst()){
			while (resultSet.next()) {
				actualColumnValue[iterator] = resultSet.getString("EVENT_CODE")
				desiredValue = actualColumnValue[iterator]
				iterator++
				TN = resultSet.getString("TN")
				trans_OID = resultSet.getString("TRANS_PARENT_OID")
				parent_OID = resultSet.getString("PARENT_OID")
				tc.setPropertyValue("CUSTOMERID", resultSet.getString("CUSTOMERID"))
				tc.setPropertyValue("SUPPLIER_NAME", resultSet.getString("SUPPLIER_NAME"))
				tc.setPropertyValue("ONSP_SPID", resultSet.getString("ONSP_SPID"))
				tc.setPropertyValue("NNSP_SPID", resultSet.getString("NNSP_SPID"))
				tc.setPropertyValue("CSR_TXNUM", resultSet.getString("CSR_TXNUM"))
				tc.setPropertyValue("TN", resultSet.getString("TN").replace("-",""))
				tc.setPropertyValue("TRANS_PARENT_OID", trans_OID)
				tc.setPropertyValue("PARENT_OID",parent_OID)
				dbFlag = true
//				if (desiredValue.contains("Reject") || desiredValue.contains("failed")){
//					rejectFailflag = false 
//					break; 
//				}
			}
		}
		if(!rejectFailflag)
		break;

		//Explicit Delay
		tcGen.testSteps["Delay"].run(testRunner, context)
		totaltime = totaltime + 2000
		if (totaltime >= timeOut) {
			break;
		}
	   }
	   if(!actualColumnValue.contains(ExpectedColumnValues[l])){
	   	break;
	   }
	}
	log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: Actual Result: Column Name: EVENT CODE & Actual Values: ' + actualColumnValue
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: Actual Result: Column Name: EVENT CODE & Actual Values: " + actualColumnValue)
	//Validation of Event Codes in TX_Event History 
	boolean flag
	boolean schedulerFlag = true
	List<String> failedValues = new ArrayList<String>()
	if (ExpectedColumnValues.size() != 0 && actualColumnValue.size() != 0){
		for (def actualValue: ExpectedColumnValues){
			flag = false
			for (iterator = 0; iterator < actualColumnValue.size(); iterator++){
				actualColumnValue[iterator]
				if (actualColumnValue[iterator].equalsIgnoreCase(actualValue)){
//					//If Status shows Pending then running scheduler after updating the timestamp in Holding Table
//					dbQuery = "Select * from SEA_TRANSACTION_HOLDING where TN = '" + TN + "'"
//					resultSet = dbConnection.getResultSet(con, dbQuery)
//					if (resultSet.next()){
//						if (schedulerFlag){
//							dbQuery = "Update SEA_TRANSACTION_HOLDING set DATETIME = SYSDATE where TN = '" + TN + "'"
//							log.info '[RUNNING SCHEDULER]: Query to Update records in SEA_TRANSACTION_HOLDING table:-' + dbQuery
//							inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[RUNNING SCHEDULER]: Query to Update records in SEA_TRANSACTION_HOLDING table:-" + dbQuery)
//							resultSet = dbConnection.getResultSet(con, dbQuery)
//							con.setAutoCommit(false)
//							con.commit()
//							tcBus.testSteps["Scheduler"].run(testRunner, context)
//							log.info '[RUNNING SCHEDULER]:Scheduler executed successfully'
//							inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[RUNNING SCHEDULER]:Scheduler executed successfully")
//							schedulerFlag = false
//						}
//					}
					flag = true;
					break;
				}
			}
			if(!flag){
				log.error '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: Expected Event Code :' + actualValue +" is not present in actual result :"+  actualColumnValue
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: Expected Event Code :" + actualValue +" is not present in actual result :"+  actualColumnValue)
				failedValues.add(actualValue)
			}
		}
	} else {
		failedValues.add("Failed")	
	}
	if(!ExpectedValue.contains("av")) {
		tc.setPropertyValue("dbValFlag", flag.toString())
	}
	tc.setPropertyValue("EventCodes",actualColumnValue.toString())

	//Validation in ESR fullfillment table in last iteration
	def ESRfullfillmentflag = context.expand('${#TestCase#ESRfullfillmentflag}')
	def supFlag = context.expand('${#TestCase#SupFlag}')
	if (ESRfullfillmentflag.equalsIgnoreCase("true") && (supFlag.equalsIgnoreCase("null"))){
	   if(!orderSubType.equalsIgnoreCase("Reclaim") && !orderSubType.equalsIgnoreCase("PORT OUT")){
		for (def data: actualColumnValue){
			if (data.equalsIgnoreCase("csr_successful") ||data.equalsIgnoreCase("focaccept") || data.equalsIgnoreCase("soa_complete") || data.equalsIgnoreCase("dl_complete") ||data.equalsIgnoreCase("e911_complete") || data.equalsIgnoreCase("lidb_complete")) {
				status = data;
			}
			else{
				continue;
			}

			def ESRQuery = "select REQUEST_DT,RESPONSE_DT from SEA_ESR_FULFILLMENT where Trans_OID ='" + trans_OID + "' and STATUS = '" + status + "'";
			log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: Validation of Request and Response DT, query executed in SEA_ESR_FULLFILLMENT:-' + ESRQuery
			inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: Validation of Request and Response DT, query executed in SEA_ESR_FULLFILLMENT:-" + ESRQuery)
			resultSet = dbConnection.getResultSet(con, ESRQuery)
			if (resultSet.isBeforeFirst()){
				tc.setPropertyValue("tcResultsCollection","")  
				while (resultSet.next()) {
					req_Dt = resultSet.getString("REQUEST_DT")
					res_Dt = resultSet.getString("RESPONSE_DT")
					esrFlag = true
				}
			}
			if (req_Dt != null && res_Dt != null){ 
				log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: REQUEST DT Value for '+ status + ':'+  req_Dt +'\nRESPONSE DT VALUE for ' + status + ': '+  res_Dt +'\n'
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: REQUEST DT Value for "+ status + ":"+  req_Dt +"\nRESPONSE DT VALUE for " + status + ": "+  res_Dt)
			}else {
				log.error '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: REQUEST DT Value for '+ status + ':'+  req_Dt +'\nRESPONSE DT VALUE for ' + status + ': '+  res_Dt +'\n'
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: REQUEST DT Value for "+ status + ":"+  req_Dt +"\nRESPONSE DT VALUE for " + status + ": "+  res_Dt)
				esrFlag = false
			}
		}
	}
	if(tcName.contains("Disconnect") && tcName.contains("Portin") && customer.equalsIgnoreCase("TWC")) {
        dbQuery = "select STATUS from SEA_DISCO where PON='" + context.expand('${#TestCase#PON}') + "'"
        resultSet = dbConnection.getResultSet(con,dbQuery)
        log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: Valiadation in SEA_DISCO Table, Query Executed ' + dbQuery
        inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: Validation in SEA_DISCO Table, Query Executed : " + dbQuery)
        iterator = 0
        while(resultSet.next()){
              seaDiscoActualValues[iterator] = resultSet.getString("STATUS")
        }
        if(seaDiscoActualValues.size()!=0){
              flag=true;
              log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']:Result for STATUS column in SEA DISCO TABLE' + seaDiscoActualValues[iterator] + ' & Actual Values: ' + seaDiscoActualValues[iterator]
              inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: Result for STATUS column in SEA DISCO TABLE & Actual Values: " + seaDiscoActualValues[iterator])
        }
		else{
		   flag = false;
		   log.error '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']:NO RESULTS FOUND IN DB'
		   inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: NO RESULTS FOUND IN DB")
	  }
	 }

	if(tcName.contains("Reclaim")){
		dbQuery = "select STATUS from SEA_DISCO where PON='" + context.expand('${#TestCase#PON}') + "'"
		resultSet = dbConnection.getResultSet(con,dbQuery)
		log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: Valiadation in SEA_DISCO Table, Query Executed ' + dbQuery
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: Validation in SEA_DISCO Table, Query Executed : " + dbQuery)
		iterator = 0
		while(resultSet.next()){
			seaDiscoActualValues[iterator] = resultSet.getString("STATUS")
		}
		if(seaDiscoActualValues.size()==0){
			flag=true;
			log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']:Result for STATUS column in SEA DISCO TABLE SHOULD BE NULL'
			inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: Result for STATUS column in SEA DISCO TABLE SHOULD BE NULL ")
		}
		else{
			flag = false;
			log.error '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']:RESULTS FOUND IN DB'
			inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: RESULT IN DB: " +seaDiscoActualValues[iterator])
		}
	}

	}

	//Logging the final status 
	if (failedValues.size>0 || !esrFlag || !flag) {
		log.error '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: FAIL'
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: FAIL")
	}else {
		log.info '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: PASS'
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SEA PEP DB VALIDATION ITERATION " + dbValCounter + "]: PASS")
	}
	con.close()
}catch (Exception e){
	log.error '[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: Unable to perform SEA PEP DB Validation - ' + e.getMessage()
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ':ERROR:[SEA PEP DB VALIDATION ITERATION ' + dbValCounter + ']: Unable to perform SEA PEP DB Validation - ' + e.getMessage())
} finally {
	con.close()	
}

