//Defining fileName and input file for logging
def fileName = context.expand('${#TestCase#FileName}')
def inputFile = new File(fileName)
def tc = testRunner.testCase
def tcGen = testRunner.testCase.testSuite.testCases["Generic Utilities_" + tc.name.split("_")[1]]
//Defining flag
boolean flag = false
Connection con

try {

//Reading DB Properties
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
def SEAPEPdbUrl = context.expand('${#TestCase#SEA_PEP_DB_CONNECTION_URL}')
def SEAPEPdbDriver = context.expand('${#TestCase#SEA_PEP_DB_DRIVER}')
def SEAPEPdbUser = context.expand('${#TestCase#SEA_PEP_DB_USERNAME}')
def SEAPEPdbKey = context.expand('${#TestCase#SEA_PEP_DB_PASSWORD_KEY}')
def SEAPEPdbEncryptedKey = context.expand('${#TestCase#SEA_PEP_DB_PASSWORD_ENCRYPTED_KEY}')

def CHdbUrl = context.expand('${#TestCase#CH_DB_CONNECTION_URL}')
def CHdbDriver = context.expand('${#TestCase#CH_DB_DRIVER}')
def CHdbUser = context.expand('${#TestCase#CH_DB_USERNAME}')
def CHdbKey = context.expand('${#TestCase#CH_DB_PASSWORD_KEY}')
def CHdbEncryptedKey = context.expand('${#TestCase#CH_DB_PASSWORD_ENCRYPTED_KEY}')

def dbUrl = context.expand('${#TestCase#DB_CONNECTION_URL}')
def dbDriver = context.expand('${#TestCase#DB_DRIVER}')
def dbUser = context.expand('${#TestCase#DB_USERNAME}')
def dbKey = context.expand('${#TestCase#DB_PASSWORD_KEY}')
def dbEncryptedKey = context.expand('${#TestCase#DB_PASSWORD_ENCRYPTED_KEY}')

def Adapter = context.expand('${#TestCase#Adapter}')
def orderID = context.expand('${#TestCase#ORDERID}')
def pon = context.expand('${#TestCase#PON}')
def onsp = context.expand('${#TestCase#ONSP}')
def customerid = context.expand('${#TestCase#CUSTOMERID}')
def tranparentoid = context.expand('${#TestCase#TRANS_PARENT_OID}')
def dlProvider = context.expand('${#TestCase#DLProvider}')
def lsrRemark = context.expand('${#TestCase#LSRRemark}')
def sup = context.expand('${#TestCase#SUP}')

//Decrypting Password
tcGen.testSteps["Password Decryption"].run(testRunner, context)
def password = context.PasswordDecryption

// Connection Setup
tcGen.testSteps["DB Connection"].run(testRunner, context)
dbConnection = context.DBConnection

Map < String, String > validationStatus = new HashMap < String, String > ();
Map < String, String > nodeNameAndValue = new HashMap < String, String > ();
def DataBase
ResultSet resultSet
def DBValidationInXml = context.expand('${#TestCase#XMLValidationInDB}').split("\\|\\|")
for (i = 0; i < DBValidationInXml.size(); i++) {
  def DBQueryXpathDetails = DBValidationInXml[i].split("\\|")
  DataBase = DBQueryXpathDetails[0]
  switch (DataBase) {
   case "SEA_PEP_DB":
    def SEAPEPdbPassword = password.decryptPassword(SEAPEPdbKey, SEAPEPdbEncryptedKey)
    dbConnection.setDBProperties(SEAPEPdbDriver, SEAPEPdbUrl, SEAPEPdbUser, SEAPEPdbPassword)
    con = dbConnection.getDBConnection()
    break;
   case "CH_DB":
    def CHdbPassword = password.decryptPassword(CHdbKey, CHdbEncryptedKey)
    dbConnection.setDBProperties(CHdbDriver, CHdbUrl, CHdbUser, CHdbPassword)
    con = dbConnection.getDBConnection()
    break;
    case "ADAPTER_DB":
    def dbPassword = password.decryptPassword(dbKey, dbEncryptedKey)
    dbConnection.setDBProperties(dbDriver, dbUrl, dbUser, dbPassword)
    con = dbConnection.getDBConnection()
    break;
  }

  dbQuery = DBQueryXpathDetails[1]
  Matcher matcher = Pattern.compile("\\\$(\\w+)").matcher(dbQuery);
  while (matcher.find()) {
   String actualStr = matcher.group(1);
   dbQuery = dbQuery.replace("\$" + actualStr, "'" + testRunner.testCase.getPropertyValue(actualStr) + "'");
  }

  Thread.sleep(15000)
  resultSet = dbConnection.getResultSet(con, dbQuery)
  String xml_data
  def xpathExpr
  if (resultSet.next()) {
   log.info '[LOGGED XML DB VALIDATION]: Query Executed in ' + DataBase + " is " + dbQuery
   inputFile.append("\r\n" + (new java.util.Date()).toString() + ":INFO:[LOGGED XML DB VALIDATION]: Query Executed in " + DataBase + " is " + dbQuery)
   xml_data = resultSet.getString(1)
   InputStream XmlParse = new ByteArrayInputStream(xml_data.getBytes("UTF-8"));
   DocumentBuilderFactory docBuilderFactory = DocumentBuilderFactory.newInstance()
   DocumentBuilder docBuilder = docBuilderFactory.newDocumentBuilder()
   Document document = docBuilder.parse(XmlParse)
   xpath = XPathFactory.newInstance().newXPath()
   def xPathAndValuesToBeValidate = DBQueryXpathDetails[2].split("\\#\\#")
   for (int j = 0; j < xPathAndValuesToBeValidate.size(); j++) {
    String nodeName = ""
    String nodeValue = ""
    xpathExpr = xPathAndValuesToBeValidate[j].split("\\{")[0];
    def ExpectedValue = xPathAndValuesToBeValidate[j].split("\\{")[1].split("\\}")[0];
    if (ExpectedValue.startsWith("\$")) {
     ExpectedValue = ExpectedValue.replace("\$", "")
     ExpectedValue = testRunner.testCase.getPropertyValue(ExpectedValue)
   }
    NodeList nodes = (NodeList) xpath.evaluate(xpathExpr, document, XPathConstants.NODESET)
    if(Adapter.equalsIgnoreCase("COMCAST DL")){
   	nodes = document.getElementsByTagName(xpathExpr);
    }
    for (Node node in nodes) {
     nodeName = node.getNodeName()
     nodeValue = node.getTextContent()
     if (node.getAttribute("value") != "") {
      nodeValue = node.getAttribute("value")
     }
     nodeValue=nodeValue.replaceAll("^[ |\t]*\n" ,"")
    }

    if (nodeName != "") {
                tc.setPropertyValue(nodeName,nodeValue)
     if (ExpectedValue.equalsIgnoreCase(nodeValue)) {
      flag = true
      log.info '[LOGGED XML DB VALIDATION]:' + DataBase + " Expected Node " + nodeName + " and Expected Value : " + ExpectedValue + " Actual Node Value : " + nodeValue + " Validation Status is : PASS"
      inputFile.append("\r\n" + (new java.util.Date()).toString() + ":INFO:[LOGGED XML DB VALIDATION]: " + DataBase + " Expected Node " + nodeName + " and Expected Value : " + ExpectedValue + " Actual Node Value : " + nodeValue + " Validation Status is : PASS")
     }
     else if (ExpectedValue.equalsIgnoreCase("BLANK") && nodeValue.equals("")){
                     log.info '[LOGGED XML DB VALIDATION]: ' + DataBase + " Expected Node name at XPATH : " + xpathExpr + " is present in XML and Value is BLANK as Expeceted, Validation Status is : PASS"
                     inputFile.append("\r\n" + (new java.util.Date()).toString() + ":INFO:[LOGGED XML DB VALIDATION]: " + DataBase + " Expected Node name at XPATH " + xpathExpr + " is present in XML and Value is BLANK as Expeceted, Validation Status is : PASS")
                     flag=true
                }
                else if(ExpectedValue.equalsIgnoreCase("BLANK") && !nodeValue.equals("")){              
//                            log.error '[LOGGED XML DB VALIDATION]: ' + DataBase + " Expected Node " + nodeName + " and Expected Value : " + ExpectedValue + " Actual Node Value : " + nodeValue + " Validation Status is : FAIL"
//                            inputFile.append("\r\n" + (new java.util.Date()).toString() + ":ERROR:[LOGGED XML DB VALIDATION]: " + DataBase + " Expected Node " + nodeName + " and Expected Value : " + ExpectedValue + " Actual Node Value : " + nodeValue + " Validation Status is : FAIL")
                                flag=false              
                }
                else {
      flag = false
     }
    }
    else{
                flag=false
    }

if (ExpectedValue.equalsIgnoreCase("Exist") && !flag) {
     log.info '[LOGGED XML DB VALIDATION]: ' + DataBase + " Expected Node name at XPATH : " + xpathExpr + " is present in XML, Validation Status is : PASS"
     inputFile.append("\r\n" + (new java.util.Date()).toString() + ":INFO:[LOGGED XML DB VALIDATION]: " + DataBase + " Expected Node name at XPATH " + xpathExpr + " is present in XML, Validation Status is : PASS")
     flag=true
}
else if (ExpectedValue.equalsIgnoreCase("NotExist") && !flag) {
                log.info '[LOGGED XML DB VALIDATION]: ' + DataBase + " Expected Node name at XPATH : " + xpathExpr + " is Not present in XML, Validation Status is : PASS"
     inputFile.append("\r\n" + (new java.util.Date()).toString() + ":INFO:[LOGGED XML DB VALIDATION]: " + DataBase + " Expected Node name at XPATH " + xpathExpr + " is Not present in XML, Validation Status is : PASS")
     flag=true
}
else if(!flag && nodeName != ""){
                log.error '[LOGGED XML DB VALIDATION]: ' + DataBase + " Expected Node " + nodeName + " and Expected Value : " + ExpectedValue + " Actual Node Value : " + nodeValue + " Validation Status is : FAIL"
     inputFile.append("\r\n" + (new java.util.Date()).toString() + ":ERROR:[LOGGED XML DB VALIDATION]: " + DataBase + " Expected Node " + nodeName + " and Expected Value : " + ExpectedValue + " Actual Node Value : " + nodeValue + " Validation Status is : FAIL")
                flag=false
}
else if(!flag){
                log.error '[LOGGED XML DB VALIDATION]: ' + DataBase + " Expected Node name at XPATH: " + xpathExpr + " is Not present in XML, Validation Status is : FAIL"
     inputFile.append("\r\n" + (new java.util.Date()).toString() + ":ERROR:[LOGGED XML DB VALIDATION]: " + DataBase + " Expected Node name at XPATH: " + xpathExpr + " is Not present in XML Validation Status is : FAIL")
                flag=false
}
}
  } else {
   flag = false
   log.error '[LOGGED XML DB VALIDATION]: ' + DataBase + " Expected Result XML not present in DB Check your Query : " + dbQuery + " again in DB, Validation Status is : FAIL"
   inputFile.append("\r\n" + (new java.util.Date()).toString() + ":INFO:[LOGGED XML DB VALIDATION]: " + DataBase + " Expected Result XML not present in DB Check your Query : " + dbQuery + " again in DB Validation Status is : FAIL")
  }

  if (flag) {
   log.info '[LOGGED XML DB VALIDATION]: PASS'
   inputFile.append("\r\n" + (new java.util.Date()).toString() + ":INFO:[LOGGED XML DB VALIDATION]: PASS")
  } else {
   log.error '[LOGGED XML DB VALIDATION]: FAIL'
   inputFile.append("\r\n" + (new java.util.Date()).toString() + ":ERROR:[LOGGED XML DB VALIDATION]: FAIL")
  }
}
} catch (Exception e) {
log.error "[LOGGED XML DB VALIDATION]:Unable to perform Data Validation - " + e.getMessage()
inputFile.append("\r\n" + (new java.util.Date()).toString() + ":ERROR:[LOGGED XML DB VALIDATION]: Unable to perform Data Validation - " + e.getMessage())
} finally {
con.close()
}

