try
{
	//Reading DB Properties
	def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
	def tc = testRunner.testCase
	def tcGen = testRunner.testCase.testSuite.testCases["Generic Utilities_" + tc.name.split("_")[1]]
	def AQADMINdbUrl = context.expand('${#TestCase#AQ_DB_CONNECTION_URL}')
	def AQADMINdbDriver = context.expand('${#TestCase#AQ_DB_DRIVER}')
	def AQADMINdbUser = context.expand('${#TestCase#AQ_DB_USERNAME}')
	def AQADMINdbKey = context.expand('${#TestCase#AQ_DB_PASSWORD_KEY}')
	def AQADMINdbEncryptedKey = context.expand('${#TestCase#AQ_DB_PASSWORD_ENCRYPTED_KEY}')	
	def Adapter = context.expand('${#TestCase#Adapter}')
	def orderID = context.expand('${#TestCase#ORDERID}')

	//Decrypting Password
	tcGen.testSteps["Password Decryption"].run(testRunner, context)
	def password = context.PasswordDecryption
	def AQADMINdbPassword = password.decryptPassword(AQADMINdbKey,AQADMINdbEncryptedKey)

	// Connection Setup
	tcGen.testSteps["DB Connection"].run(testRunner, context)
	def dbConnection = context.DBConnection
	dbConnection.setDBProperties(AQADMINdbDriver,AQADMINdbUrl,AQADMINdbUser,AQADMINdbPassword)
	Connection con = dbConnection.getDBConnection()

	//Initializing Query
	dbQuery = "UPDATE TRIGGER_IC_QUEUE SET DELAY = SYSDATE-1 ,STATE = '0' WHERE CORRID = '"+context.expand('${#TestCase#PON}')+"'"
	log.info '[AQ ADMIN DB VALIDATION]: Query executed to update delay time:-' + dbQuery
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[AQ ADMIN DB VALIDATION]: Query executed to update delay time:-" + dbQuery)
	def orderSubType = context.expand('${#TestCase#OrderSubType}')
	ResultSet resultSet = dbConnection.getResultSet(con,dbQuery)
	resultSet = dbConnection.getResultSet(con,dbQuery)
	con.setAutoCommit(false)
	con.commit()
	//log.info '[AQ ADMIN DB VALIDATION]: Delay time updated'

}catch(Exception e){
	log.error "[AQ ADMIN DB VALIDATION]: Unable to perform DB Validation - " + e.getMessage()
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[AQ ADMIN DB VALIDATION]: Unable to perform DB Validation - " + e.getMessage())
}

