	def CHdbEncryptedKey = context.expand('${#TestCase#CH_DB_PASSWORD_ENCRYPTED_KEY}')

	//Decrypting Password
	tcGen.testSteps["Password Decryption"].run(testRunner, context)
	def password = context.PasswordDecryption
	def CHdbPassword = password.decryptPassword(CHdbKey,CHdbEncryptedKey)

	// Connection Setup
	tcGen.testSteps["DB Connection"].run(testRunner, context)
	def dbConnection = context.DBConnection
	dbConnection.setDBProperties(CHdbDriver,CHdbUrl,CHdbUser,CHdbPassword)
	Connection con = dbConnection.getDBConnection()

	def TN= npa+ nxx + lineNum
     tc.setPropertyValue("TN", TN)

      def dbTabColVal=context.expand('${#TestCase#COLUMN_VALUE}').split("\\|\\|")
      def dbTable=context.expand('${#TestCase#TABLE_NAME}').split("\\|\\|")


      for(int i=0;i<dbTable.size();i++)
      {	     		
		dbQuery = context.expand('${#TestCase#'+dbTable[i]+'}')
		ExpectedValue = dbTabColVal[i]

		dbQuery=dbQuery.replace("\$CSR_TXNUM","'"+csrtxnum+"'")
		dbQuery=dbQuery.replace("\$CUSTOMERID","'"+customerid+"'")	
		dbQuery=dbQuery.replace("\$ILEC","'"+ilec+"'")
		dbQuery=dbQuery.replace("\$PON","'"+pon+"'")
		dbQuery=dbQuery.replace("\$ONSP","'"+onsp+"'")
		dbQuery=dbQuery.replace("\$TN","'"+TN+"'")
		dbQuery=dbQuery.replace("\$Customer","'"+customer+"'")

		ResultSet resultSet = dbConnection.getResultSet(con,dbQuery)
		log.info '[CH DB VALIDATION '+CHDBIterator+']: ' + dbQuery
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[CH DB VALIDATION " + CHDBIterator + "]:" + dbQuery)

		def ActualColumnData = []

		def name = []	
	 	def msgXML
	 	rsmd = resultSet.getMetaData()
	 	colSize=rsmd.getColumnCount()

		while (resultSet.next()){
			for(int col=0;col<colSize;col++)
	           {
	           ActualColumnData[col] = resultSet.getString(col+1)           
	           name[col] = rsmd.getColumnName(col+1)
	           }  
	     }
	     if(ActualColumnData.size()!=0){
	     	flag=true;
	     	log.info '[CH DB VALIDATION '+CHDBIterator+']: DB Result: Column Name: ' + name + ' & Actual Values: ' + ActualColumnData
	     	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[CH DB VALIDATION " + CHDBIterator + "]: DB Result: Column Name:" + ActualColumnData)
	     }
	     else{
	     	flag = false;
	     	log.error '[CH DB VALIDATION '+CHDBIterator+']:NO RESULTS FOUND IN DB'
	     	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[CH DB VALIDATION " + CHDBIterator + "]: NO RESULTS FOUND IN DB")
	     }
	  }

	if(flag){
		log.info '[CH DB VALIDATION '+CHDBIterator+']: PASS' 
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[CH DB VALIDATION " + CHDBIterator + "]: PASS")
		}
	else{
		log.error '[CH DB VALIDATION '+CHDBIterator+']: FAIL'
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[CH DB VALIDATION " + CHDBIterator + "]: FAIL")
		}

}catch (Exception e){
	log.error '[CH DB VALIDATION '+CHDBIterator+']: Unable to perform DB Validation - ' + e.toString()
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[CH DB VALIDATION " + CHDBIterator + "]:Unable to perform DB Validation -" + e.toString())
}   


//Method to read child nodes of XML    
String  readNode(NodeList nodeList) {
                def tc = testRunner.testCase
                def resFlow=context.expand('${#TestCase#Response_Flow}')
     for (int count = 0; count < nodeList.getLength(); count++) {
     Node tempNode = nodeList.item(count)
     // make sure it's element node.
       if (tempNode.getNodeType() == Node.ELEMENT_NODE) {
                       // get node name and value
             if(!tempNode.getTextContent().contains("\t")){
                             tc.setPropertyValue(tempNode.getNodeName(), tempNode.getTextContent())
             }
             if (tempNode.hasChildNodes()) {
                             // loop again if has child nodes
                             readNode(tempNode.getChildNodes())
             }
       }
    }              
}

