def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context);
def tc=testRunner.testCase
def tcGen = testRunner.testCase.testSuite.testCases["Generic Utilities_" + tc.name.split("_")[1]]
def ProjectPath = groovyUtils.projectPath
def scenario = context.expand('${#TestCase#ScenrioName}')
def adapter =context.expand('${#TestCase#Adapter}')
def customerid = context.expand('${#TestCase#CUSTOMERID}')
def csrtxnum = context.expand('${#TestCase#CSR_TXNUM}')
def ilec = context.expand('${#TestCase#ILEC}')
def pon = context.expand('${#TestCase#PON}')
def TN = context.expand('${#TestCase#TN}')
def npa = context.expand('${#TestCase#NPA}')
def nxx = context.expand('${#TestCase#NXX}')
def linenumber = context.expand('${#TestCase#LineNumber}')
def SOADBIterator=context.expand('${#TestCase#SOADBIterator}')
def mockResponse = context.expand('${#TestCase#MockResponse}')
def fileName = context.expand('${#TestCase#FileName}')
def inputFile = new File(fileName)

try
{
	def SOAdbUrl = context.expand('${#TestCase#SOA_DB_CONNECTION_URL}')
	def SOAdbDriver = context.expand('${#TestCase#SOA_DB_DRIVER}')
	def SOAdbUser = context.expand('${#TestCase#SOA_DB_USERNAME}')
	def SOAdbKey = context.expand('${#TestCase#SOA_DB_PASSWORD_KEY}')
	def SOAdbEncryptedKey = context.expand('${#TestCase#SOA_DB_PASSWORD_ENCRYPTED_KEY}')

	//Decrypting Password
	tcGen.testSteps["Password Decryption"].run(testRunner, context)
	def password = context.PasswordDecryption
	def SOAdbPassword = password.decryptPassword(SOAdbKey,SOAdbEncryptedKey)

	// Connection Setup
	tcGen.testSteps["DB Connection"].run(testRunner, context)
	def dbConnection = context.DBConnection
	dbConnection.setDBProperties(SOAdbDriver,SOAdbUrl,SOAdbUser,SOAdbPassword)
	Connection con = dbConnection.getDBConnection()

	//Reading Queries
     String csvFile = ProjectPath+'\\DATABASE VALIDATION CONFIGURATION\\Expected_Validation_CH & SOA.csv'
	BufferedReader br = null
     String line = ""
     String cvsSplitBy = ","
	   br = new BufferedReader(new FileReader(csvFile))
	   String lineReader ="";

	   lineReader = br.readLine()
	   char[] str = lineReader.toCharArray();
	   int colSize=0
	   for(int i=0;i<lineReader.length();i++)
	   {
	       if(str[i]==',')
	       {
	           ++colSize
	       }
	   }
	   String[] cellIterator = lineReader.split(cvsSplitBy)
     while ((line = br.readLine()) != null)
     {
		if(mockResponse.equalsIgnoreCase(line.split(cvsSplitBy,-1)[0])){
	     	// use comma as separator
	           String[] temp = line.split(cvsSplitBy,-1)
	           for(int i=1;i<=colSize;i++)
	           {
	            	testRunner.testCase.setPropertyValue(cellIterator[i],temp[i])
	           } 
	      }
      }

      def tn= npa + "-" +nxx+ "-" + linenumber+ "-"+ linenumber
      def dbTabColVal=context.expand('${#TestCase#COLUMN_VALUE}').split("\\|\\|")
      def dbTable=context.expand('${#TestCase#TABLE_NAME}').split("\\|\\|")

      for(int i=0;i<dbTable.size();i++)
      {	     		
		dbQuery = context.expand('${#TestCase#'+dbTable[i]+'}')
		ExpectedValue = dbTabColVal[i]

		dbQuery=dbQuery.replace("\$CSR_TXNUM","'"+csrtxnum +"'")
		dbQuery=dbQuery.replace("\$CUSTOMERID","'"+customerid+"'")	
		dbQuery=dbQuery.replace("\$ILEC","'"+ilec+"'")
		dbQuery=dbQuery.replace("\$PON","'"+pon+"'")
		dbQuery=dbQuery.replace("\$TN","'"+tn+"'")

		ResultSet resultSet = dbConnection.getResultSet(con,dbQuery)
		log.info '['+mockResponse+ ' DB VALIDATION]: ' + dbQuery
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[" +mockResponse+ " DB VALIDATION]: " + dbQuery)

		def ActualColumnData = []
		boolean flag	
		def name = []	
	 	def msgXML
	 	rsmd = resultSet.getMetaData()
	 	colSize=rsmd.getColumnCount()

		while (resultSet.next()){
			for(int col=0;col<colSize;col++)
	           {
		           ActualColumnData[col] = resultSet.getString(col+1)           
		           name[col] = rsmd.getColumnName(col+1)
	           }  
	     }
	     if(ActualColumnData.size()!=0){     	
	     	log.info '['+mockResponse+ ' DB VALIDATION]: DB Result: Column Name: ' + name + ' & Actual Values: ' + ActualColumnData
	     	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[" +mockResponse+ " DB VALIDATION]: DB Result: Column Name:" + name + " & Actual Values: " + ActualColumnData)

	     	flag = true;
	     }
	     else{
	     	log.error '['+mockResponse+ ' DB VALIDATION]:NO RESULTS FOUND IN DB'
	     	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" +mockResponse+ " DB VALIDATION]: NO RESULTS FOUND IN DB")

	     	flag = false;
	     }
	  }
       	if(flag){
			log.info '['+mockResponse+ ' DB VALIDATION]: PASS'
			inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[" +mockResponse+ " DB VALIDATION]: PASS")
			}
		else{
			log.error '['+mockResponse+ ' DB VALIDATION]: FAIL'
			inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" +mockResponse+ " DB VALIDATION]: FAIL")
			}
}catch (Exception e){
	log.error '['+mockResponse+ ' DB VALIDATION]: Unable to perform DB Validation - ' + e.toString()
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[" +mockResponse+ " DB VALIDATION]:" + e.toString())
}

