Document xmlDocument = null;
def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def product = context.expand('${#Project#Product}')
def adapter = context.expand('${#TestCase#Adapter}')
def project = testRunner.testCase.testSuite.project
def tc = testRunner.testCase;
def test_Step = context.expand('${#TestCase#InputXmlType}')
def npQTY = context.expand('${#TestCase#NPQTY}')
def maxTnNum
def test_Case = adapter + "_REQUEST_TEMPLATES"
def req_XML
try {
    if(npQTY.equalsIgnoreCase("")){
    		npQTY = 0
    }else{
    		npQTY = Integer.parseInt(npQTY) - 1
    }
    req_XML = project.getTestSuiteByName("ADAPTERS_REQUEST_TEMPLATES").getTestCaseByName(test_Case).getTestStepByName(test_Step).getPropertyValue("Request")
    InputStream XmlParse = new ByteArrayInputStream(req_XML.getBytes("UTF-8"));
    DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
    DocumentBuilder builder = factory.newDocumentBuilder();
    xmlDocument = builder.parse(XmlParse)

    if (test_Step.equalsIgnoreCase("AccountChange_Commercial") ||test_Step.equalsIgnoreCase("AccountChange_CommercialMultiTN")|| test_Step.equalsIgnoreCase("Keep_Commercial") || test_Step.equalsIgnoreCase("Transfer_Commercial") || test_Step.contains("MCC_MTN")) {

        Node nodes = xmlDocument.getDocumentElement();
        if (nodes.hasChildNodes()) {
            NodeList list = nodes.getChildNodes();
            for (int i = 0; i < list.getLength(); i++) {
                if (list.item(i).hasChildNodes()) {
                    for (int j = 0; j < list.item(i).getChildNodes().getLength(); j++) {
                        if (list.item(i).getChildNodes().item(j).getNodeName() == "sea:ProductDetails") {
                            req_XML = repeatSeaChildNodes(xmlDocument, npQTY);

                        }
                    }
                }
            }
        }
        req_XML = repeatSeaChilds(xmlDocument, npQTY);
        tc.setPropertyValue("Request", req_XML)
        maxTnNum = maxTnNumber(req_XML)
        tc.setPropertyValue("MaxTnNumber", maxTnNum)


    }

    if (test_Step.equalsIgnoreCase("Portin_Commercial") || test_Step.equalsIgnoreCase("Portout_Commercial") || test_Step.equalsIgnoreCase("Dir_Listing_Commercial") || test_Step.equalsIgnoreCase("Install_Complete") || test_Step.equalsIgnoreCase("Disconnect_Commercial") || test_Step.equalsIgnoreCase("Native_Commercial")) {

        Node nodes = xmlDocument.getDocumentElement();
        if (nodes.hasChildNodes()) {
            NodeList list = nodes.getChildNodes();
            for (int i = 0; i < list.getLength(); i++) {
                if (list.item(i).hasChildNodes()) {
                    for (int j = 0; j < list.item(i).getChildNodes().getLength(); j++) {
                        if (list.item(i).getChildNodes().item(j).getNodeName() == "ProductDetails") {
                            req_XML = repeatChildNodes(xmlDocument, npQTY);

                        }
                    }
                }
            }
        }

        if (test_Step.equalsIgnoreCase("Portin_Commercial") || test_Step.equalsIgnoreCase("Portout_Commercial") || test_Step.equalsIgnoreCase("Install_Complete") || test_Step.equalsIgnoreCase("Native_Commercial") || test_Step.equalsIgnoreCase("Disconnect_Commercial")){
            req_XML1 = repeatSeaChilds(xmlDocument, npQTY);
            tc.setPropertyValue("Request", req_XML1)
            maxTnNum = maxTnNumber(req_XML1)
            tc.setPropertyValue("MaxTnNumber", maxTnNum)


        } else {
            tc.setPropertyValue("Request", req_XML)
            maxTnNum = maxTnNumber(req_XML)
            tc.setPropertyValue("MaxTnNumber", maxTnNum)
        }
    }
    if (adapter.equalsIgnoreCase("COMCAST DL") && test_Step.contains("MTN")) {
        req_XML = repeatTreatmentBlockNodes(xmlDocument, npQTY);
        tc.setPropertyValue("Request", req_XML)
    }
    if (adapter.equalsIgnoreCase("SDL") && test_Step.contains("MTN")) {
        req_XML = SDLFirstDuplicateNodes(xmlDocument, npQTY);
        req_XML = SDLSecondDuplicateNodes(xmlDocument, npQTY);
        tc.setPropertyValue("Request", req_XML)
    }
    if (adapter.equalsIgnoreCase("SDL") && test_Step.contains("MixedONSP")) {
		req_XML = SDLFirstDuplicateNodesMixedONSP(xmlDocument, npQTY);
		req_XML = SDLSecondDuplicateNodesMixedONSP(xmlDocument, npQTY);
		tc.setPropertyValue("Request", req_XML)
    }
    if(test_Step.contains("BWC_MTN") || test_Step.contains("RLEC_MTN")){
    	req_XML = repeatSeaChilds(xmlDocument, npQTY);
     tc.setPropertyValue("Request", req_XML)
    }

     if(adapter.equalsIgnoreCase("CHARTER") && test_Step.contains("MultiTN")){
    	req_XML = charterRepeatNodes(xmlDocument, npQTY);
     tc.setPropertyValue("Request", req_XML)
    }

} catch (Exception e) {
	log.error "[REPEATING NODES]:Unable to repeat the nodes:-" + e.getMessage()
	}
String maxTnNumber(String xml) {
    Document doc = DocumentBuilderFactory.newInstance().newDocumentBuilder().parse(new InputSource(new StringReader(xml)));
    NodeList nl = doc.getElementsByTagName("TN");
    def maxLine
    for (int i = 0; i < nl.getLength(); i++) {
        maxLine = nl.item(i).getTextContent();
    }
    return maxLine;
}


String repeatChildNodes(Document xmlDocument, int size) throws ParserConfigurationException, SAXException, IOException, TransformerException {

    String rootNodeItem = null;
    List < String > nodeItems = new ArrayList < String > ();
    List < String > nodeValues = new ArrayList < String > ();
    Node eNode;
    NodeList nodeList1;
    NodeList nodeList;
    int npaNum = Integer.parseInt(context.expand('${#TestCase#NPA}'))
    String npa = String.format("%03d", npaNum);
    int nxxNum = Integer.parseInt(context.expand('${#TestCase#NXX}'))
    String nxx = String.format("%03d", nxxNum);
    int lineNum = Integer.parseInt(context.expand('${#TestCase#LineNumber}'))
    //String lineNumber = String.format("%04d", lineNum);                                                        

    nodeList1 = xmlDocument.getElementsByTagName("ProductDetails");
    nodeList = xmlDocument.getElementsByTagName("TNLevelInfo");
    for (int eTemp = 0; eTemp < nodeList.getLength(); eTemp++) {
        eNode = nodeList.item(eTemp);
        rootNodeItem = eNode.getNodeName();
        if (eNode.hasChildNodes()) {
            for (int iterator = 0; iterator < eNode.getChildNodes().getLength(); iterator++) {
                Node tempNode = eNode.getChildNodes().item(iterator);
                if (tempNode.getNodeType() == Node.ELEMENT_NODE) {
                    nodeItems.add(tempNode.getNodeName());
                    nodeValues.add(tempNode.getTextContent());

                }

            }
        }

    }
    Node nodeName = null;
    Element name = null;
    def appendNodesIterator = 0;
    def tn
    while (appendNodesIterator < size) {
        nodeName = xmlDocument.createElement(rootNodeItem);
        for (int i = 0; i < nodeItems.size(); i++) {
            name = xmlDocument.createElement(nodeItems.get(i));
            if (nodeItems.get(i).contains("TN")) {
                lineNum++;
                String lineNumber = String.format("%04d", lineNum);
                tn = npa + nxx + lineNumber
                name.appendChild(xmlDocument.createTextNode(tn));
            } else {
                name.appendChild(xmlDocument.createTextNode(nodeValues.get(i)));
            }
            nodeName.appendChild(name);
        }
        Node nodel = xmlDocument.getElementsByTagName("AltAddress").item(0);
        nodeList1.item(0).insertBefore(nodeName, nodel);
        appendNodesIterator++;
    }
   return fnFormatXmlDocument(xmlDocument);
}


String repeatSeaChilds(Document xmlDocument, int size) throws TransformerException {

    String rootNode = null;
    Node newNode;
    String rootNode1 = null;
    Node newNode1;

    LinkedHashMap < String, LinkedHashMap < String, String >> nodeData = new LinkedHashMap < > ();
    LinkedHashMap < String, String > lhmTemp;
    String szParentKey = "";
    String szRemoveKey = "";

    int npaNum = Integer.parseInt(context.expand('${#TestCase#NPA}'))
    String npa = String.format("%03d", npaNum);
    int nxxNum = Integer.parseInt(context.expand('${#TestCase#NXX}'))
    String nxx = String.format("%03d", nxxNum);
    int lineNum = Integer.parseInt(context.expand('${#TestCase#LineNumber}'))
    //String lineNumber = String.format("%04d", lineNum);                   
    int locNum1 = Integer.parseInt(context.expand('${#TestCase#LOCNUM}'))
    String locNum = String.format("%03d", locNum1);
    int lNum1 = Integer.parseInt(context.expand('${#TestCase#LNUM}'))
    String lNum = String.format("%03d", lNum1);
    def test_Step = context.expand('${#TestCase#InputXmlType}')
    NodeList nodeList1;
    NodeList lsr;
    NodeList grp;

    if (test_Step.equalsIgnoreCase("Disconnect_Commercial") || test_Step.contains("BWC_MTN") || test_Step.contains("RLEC_MTN")) {
        nodeList1 = xmlDocument.getElementsByTagName("ns2:NP");
        lsr = xmlDocument.getElementsByTagName("ns2:NP_SVC_DET");
        grp = xmlDocument.getElementsByTagName("ns2:SVC_DET_GRP");
    } else {
        nodeList1 = xmlDocument.getElementsByTagName("uom:NP");
        lsr = xmlDocument.getElementsByTagName("uom:NP_SVC_DET");
        grp = xmlDocument.getElementsByTagName("uom:SVC_DET_GRP");
    }

    for (int i = 0; i < lsr.getLength(); i++) {
        newNode = lsr.item(i);
        newNode1 = grp.item(i);
        rootNode = newNode.getNodeName();
        rootNode1 = newNode1.getNodeName();
        if (newNode.hasChildNodes()) {
            for (int j = 0; j < newNode.getChildNodes().getLength(); j++) {
                Node tNode = newNode.getChildNodes().item(j);
                if (tNode.getNodeType() == Node.ELEMENT_NODE) {
                    szParentKey = tNode.getNodeName();
                    lhmTemp = new LinkedHashMap < > ();
                    if (tNode.getChildNodes().getLength() > 1) {
                        lhmTemp = fnCheckForChildNodes(lhmTemp, szParentKey,
                            szRemoveKey, tNode);

                        // break;
                    } else {
                        lhmTemp.put(szParentKey, tNode.getTextContent());
                    }
                    nodeData.put(szParentKey, lhmTemp);
                }
            }
        }
    }
    Node nodeParent = null;

    int appendNodesIterator1 = 0;
    ArrayList < String > arrayTempList = new ArrayList < String > ();
    LinkedHashMap < String, Node > lhmtempData = new LinkedHashMap < > ();
    Node nodeTempLatest = null;
    while (appendNodesIterator1 < size) {
        nodeParent = xmlDocument.createElement(rootNode);
        for (Entry < String, LinkedHashMap < String, String >> objParent: nodeData.entrySet()) {
            LinkedHashMap < String, String > lhmChild = objParent.getValue();
            ArrayList < String > arrayParentList = new ArrayList < > ();
            Node nodeLastParent = null;
            for (Entry < String, String > objChild: lhmChild.entrySet()) {
                String szKey = objChild.getKey();
                String szValue = objChild.getValue();
                if (szKey.contains("@")) {
                    StringTokenizer sztoken = new StringTokenizer(objChild.getKey(), "@");
                    ArrayList < String > arrayList = new ArrayList < String > ();

                    while (sztoken.hasMoreTokens()) {
                        arrayList.add(sztoken.nextToken());
                    }
                    int j = 0;

                    while (j < arrayList.size()) {
                        if (arrayTempList.contains(arrayList.get(j))) {
                            nodeTempLatest = lhmtempData.get(arrayList.get(j));
                        } else {
                            if (arrayList.size() - j == 1) {
                                if (arrayList.get(j).contains("uom:PORTED_NBR") || arrayList.get(j).contains("ns2:PORTED_NBR")) {
                                    lineNum++;
                                    String lineNumber = String.format("%04d", lineNum);
                                    szValue = npa + nxx + lineNumber;

                                } else if (arrayList.get(j).contains("uom:LOCNUM") || arrayList.get(j).contains("ns2:LOCNUM")) {
                                    locNum++;
                                    szValue = locNum + "";
                                } else if (arrayList.get(j).contains("uom:LNUM") || arrayList.get(j).contains("ns2:LNUM")) {
                                    lNum++;
                                    szValue = lNum + "";
                                }
                                fnAddElementToNode(xmlDocument, nodeTempLatest,
                                    arrayList.get(j), szValue);
                                nodeLastParent = nodeTempLatest;
                            } else {
                                arrayTempList.add(arrayList.get(j));
                                nodeTempLatest = xmlDocument.createElement(arrayList.get(j));
                                lhmtempData.put(arrayList.get(j), nodeTempLatest);
                                if (!arrayParentList.contains(arrayList.get(j)))
                                    arrayParentList.add(arrayList.get(j));
                            }
                        }
                        j++;
                    }

                    nodeTempLatest = nodeParent;
                } else {
                    if (szKey.contains("uom:PORTED_NBR") || szKey.contains("ns2:PORTED_NBR")) {
                        lineNum++;
                        String lineNumber = String.format("%04d", lineNum);
                        szValue = npa + nxx + lineNumber;
                    } else if (szKey.contains("uom:LOCNUM") || szKey.contains("ns2:LOCNUM")) {
                        locNum++;
                        szValue = locNum + "";
                    }
                    fnAddElementToNode(xmlDocument, nodeParent, szKey, szValue);

                    arrayTempList.clear();
                    lhmtempData.clear();
                }
            }
            Node previousNode = nodeLastParent;
            for (int i = arrayParentList.size() - 1; i >= 0; i--) {
                if (i == 0) {
                    nodeParent.appendChild(previousNode);
                } else {
                    Node node = xmlDocument.createElement(arrayParentList.get(i - 1));
                    node.appendChild(previousNode);
                    previousNode = node;
                }
            }
            arrayParentList.clear();
        }

        nodeList1.item(0).appendChild(nodeParent);

        appendNodesIterator1++;
    }
	return fnFormatXmlDocument(xmlDocument);
}

LinkedHashMap < String, String > fnCheckForChildNodes(LinkedHashMap < String, String > lhmTemp,
    String szParentKey, String szRemoveKey, Node tNode) {

    String szChildKey = "";
    for (int l = 0; l < tNode.getChildNodes().getLength(); l++) {
        Node sNode = tNode.getChildNodes().item(l);
        if (sNode.getNodeType() == Node.ELEMENT_NODE) {
            if (sNode.getChildNodes().getLength() > 1) {
                szParentKey = szParentKey + "@" + sNode.getNodeName();
                lhmTemp = fnCheckForChildNodes(lhmTemp, szParentKey, szRemoveKey, sNode);
                szParentKey = szParentKey.replaceAll("@" + sNode.getNodeName(), "");
            } else {
                szRemoveKey = "@" + sNode.getNodeName();
                szChildKey = szParentKey + szRemoveKey;
                lhmTemp.put(szChildKey, sNode.getTextContent());
            }
        }
        szChildKey = szChildKey.replaceAll(szRemoveKey, "");
    }

    return lhmTemp;
}

Node fnAddElementToNode(Document xmlDocument, Node childNode,
    String szKey, String szValue) {
    Element name1 = xmlDocument.createElement(szKey);
    name1.appendChild(xmlDocument.createTextNode(szValue.trim()));
    childNode.appendChild(name1);
    return childNode;
}

String repeatSeaChildNodes(Document xmlDocument, int size) throws ParserConfigurationException, SAXException, IOException, TransformerException {

    String rootNodeItem = null;
    List < String > nodeItems = new ArrayList < String > ();
    List < String > nodeValues = new ArrayList < String > ();
    Node eNode;
    NodeList nodeList1;
    NodeList nodeList;
    int npaNum = Integer.parseInt(context.expand('${#TestCase#NPA}'))
    String npa = String.format("%03d", npaNum);
    int nxxNum = Integer.parseInt(context.expand('${#TestCase#NXX}'))
    String nxx = String.format("%03d", nxxNum);
    int lineNum = Integer.parseInt(context.expand('${#TestCase#LineNumber}'))
    //String lineNumber = String.format("%04d", lineNum);
    nodeList1 = xmlDocument.getElementsByTagName("sea:ProductDetails");
    nodeList = xmlDocument.getElementsByTagName("sea:TNLevelInfo");
    for (int eTemp = 0; eTemp < nodeList.getLength(); eTemp++) {
        eNode = nodeList.item(eTemp);
        rootNodeItem = eNode.getNodeName();
        if (eNode.hasChildNodes()) {
            for (int iterator = 0; iterator < eNode.getChildNodes().getLength(); iterator++) {
                Node tempNode = eNode.getChildNodes().item(iterator);
                if (tempNode.getNodeType() == Node.ELEMENT_NODE) {
                    nodeItems.add(tempNode.getNodeName());
                    nodeValues.add(tempNode.getTextContent());
                }
            }
        }
    }
    Node nodeName = null;
    Element name = null;
    def appendNodesIterator = 0;

    while (appendNodesIterator < size) {

        nodeName = xmlDocument.createElement(rootNodeItem);
        for (int i = 0; i < nodeItems.size(); i++) {
            name = xmlDocument.createElement(nodeItems.get(i));
            if (nodeItems.get(i).contains("sea:TN")) {
                lineNum++;
                String lineNumber = String.format("%04d", lineNum);
                def tn = npa + nxx + lineNumber
                name.appendChild(xmlDocument.createTextNode(tn));

            } else {
                name.appendChild(xmlDocument.createTextNode(nodeValues.get(i)));
            }
            nodeName.appendChild(name);

        }
        nodeList1.item(0).appendChild(nodeName);
        appendNodesIterator++;
    }
	return fnFormatXmlDocument(xmlDocument);

}
String repeatTreatmentBlockNodes(Document xmlDocument, int size) throws TransformerException {
    int appendNodesIterator1 = 0;
    Long nfromTN = 0;
    int ntoTN = 0;
    def npa = context.expand('${#TestCase#NPA}')
    def nxx = context.expand('${#TestCase#NXX}')
    def lineNum = context.expand('${#TestCase#LineNumber}')
    def TN = npa + nxx + lineNum

    NodeList nodeList1 = xmlDocument.getElementsByTagName("dlor:DLInformation");
    NodeList lsr = xmlDocument.getElementsByTagName("base:TreatmentBlock");
    Node originalNode = null;
    for (int j = 0; j < lsr.getLength(); j++) {
        Node nTemp = lsr.item(j);
        if (nTemp.getNodeName().equals("base:TreatmentBlock")) {
            originalNode = nTemp.cloneNode(true);
            break;
        }
    }
    while (appendNodesIterator1 < size) {

        Node nodeNew = originalNode.cloneNode(true);
        String szValue = fnReadAttributes(nodeNew);
        String[] szData = szValue.split(">");
        for (int i = 0; i < szData.length; i++) {
            String[] szNew = szData[i].split("@");

            if (szNew[0].equals("fromTN")) {
                if (nfromTN == 0)
                    nfromTN = Long.valueOf((TN));
                nfromTN++;
                szNew[1] = nfromTN + "";
            } else if (szNew[0].equals("toTN")) {
                if (ntoTN == 0)
                    ntoTN = Integer.valueOf(lineNum);
                ntoTN++;
                String lineNumber = String.format("%04d", ntoTN);
                szNew[1] = lineNumber + "";
            }

            ((Element) nodeNew).setAttribute(szNew[0], szNew[1]);
        }
        nodeList1.item(0).appendChild(nodeNew);
        appendNodesIterator1++;
    }
	return fnFormatXmlDocument(xmlDocument);

}
String fnReadAttributes(Node tempNode) {
    NamedNodeMap nodeMap = tempNode.getAttributes();
    String szData = "";
    for (int i = 0; i < nodeMap.getLength(); i++) {
        Node node = nodeMap.item(i);
        szData = szData + node.getNodeName() + "@" + node.getNodeValue() + ">";
    }

    return szData;
}
String SDLFirstDuplicateNodes(Document xmlDocument, int size) throws TransformerException {
    NodeList nodeList1 = xmlDocument.getElementsByTagName("m0:LSOGnumberPortForm");
    NodeList lsr = xmlDocument.getElementsByTagName("m1:NP");
    Node originalNode = null;
    int npaNum = Integer.parseInt(context.expand('${#TestCase#NPA}'))
    String npa = String.format("%03d", npaNum);
    int nxxNum = Integer.parseInt(context.expand('${#TestCase#NXX}'))
    String nxx = String.format("%03d", nxxNum);
    int lineNum = Integer.parseInt(context.expand('${#TestCase#LineNumber}'))
    int Lnum = Integer.parseInt(context.expand('${#TestCase#LNUMBER}'))
    for (int j = 0; j < lsr.getLength(); j++) {
        Node nTemp = lsr.item(j);
        if (nTemp.getNodeName().equals("m1:NP")) {
            originalNode = nTemp.cloneNode(true);
            break;
        }
    }
    int appendNodesIterator1 = 0;
    while (appendNodesIterator1 < size) {
        Node nodeNew = originalNode.cloneNode(true);
        NodeList n1 = nodeNew.getChildNodes();
        for (int i = 0; i < n1.getLength(); i++) {
            Node tempNode = nodeNew.getChildNodes().item(i);
            if (tempNode.getNodeName().equals("m1:LNUM")) {
                Lnum++;
                String LocNum = String.format("%05d", Lnum);
                tempNode.setTextContent(LocNum);
            } else if (tempNode.getNodeName().equals("m1:PORTEDNBR")) {
                lineNum++;
                String lineNumber = String.format("%04d", lineNum);
                String tn = npa + nxx + lineNumber;
                tempNode.setTextContent(tn);
            }
        }
        nodeList1.item(0).appendChild(nodeNew);
        appendNodesIterator1++;
    }
	return fnFormatXmlDocument(xmlDocument);

}
String SDLSecondDuplicateNodes(Document xmlDocument, int size) throws TransformerException {
    NodeList nodeList1 = xmlDocument.getElementsByTagName("m0:LSOGdirectoryListingForm");
    NodeList lsr = xmlDocument.getElementsByTagName("m1:LISTING");
    Node originalNode = null;
    int npaNum = Integer.parseInt(context.expand('${#TestCase#NPA}'))
    String npa = String.format("%03d", npaNum);
    int nxxNum = Integer.parseInt(context.expand('${#TestCase#NXX}'))
    String nxx = String.format("%03d", nxxNum);
    int lineNum = Integer.parseInt(context.expand('${#TestCase#LineNumber}'))
    int Lnum = Integer.parseInt(context.expand('${#TestCase#LNUMBER}'))
    int s = 1;
    for (int j = 0; j < lsr.getLength(); j++) {
        Node nTemp = lsr.item(j);
        if (nTemp.getNodeName().equals("m1:LISTING")) {
            originalNode = nTemp.cloneNode(true);
            break;
        }
    }

    int appendNodesIterator1 = 0;
    while (appendNodesIterator1 < size) {
        Node nodeNew = originalNode.cloneNode(true);
        NodeList n1 = nodeNew.getChildNodes();
        for (int i = 0; i < n1.getLength(); i++) {
            Node tempNode = nodeNew.getChildNodes().item(i);
            if (tempNode.getNodeName().equals("m1:DLNUM")) {
                Lnum++;
                String LocNum = String.format("%04d", Lnum);
                tempNode.setTextContent(LocNum);
            } else if (tempNode.getNodeName().equals("m1:LTN")) {
                lineNum++;
                String lineNumber = String.format("%04d", lineNum);
                String tn = npa + nxx + lineNumber;
                tempNode.setTextContent(tn);
            } else if (tempNode.getNodeName().equals("m1:ALI")) {
                String ALI = tempNode.getTextContent();
                String str = ALI.substring(0, 1);
                s++;
                String ali = str + s;
                tempNode.setTextContent(ali);
            } else if (tempNode.getNodeName().equals("m1:RTY")) {
                tempNode.setTextContent("LAL");
            }
        }
        nodeList1.item(0).appendChild(nodeNew);
        appendNodesIterator1++;
    }

   return fnFormatXmlDocument(xmlDocument);
}

