    int Lnum = Integer.parseInt(context.expand('${#TestCase#LNUMBER}'))
    for (int j = 0; j < lsr.getLength(); j++) {
        Node nTemp = lsr.item(j);
        if (nTemp.getNodeName().equals("m1:NP")) {
            originalNode = nTemp.cloneNode(true);
            break;
        }
    }
    int appendNodesIterator1 = 0;
    while (appendNodesIterator1 < size) {
        Node nodeNew = originalNode.cloneNode(true);
        NodeList n1 = nodeNew.getChildNodes();
        for (int i = 0; i < n1.getLength(); i++) {
            Node tempNode = nodeNew.getChildNodes().item(i);
            if (tempNode.getNodeName().equals("m1:LNUM")) {
                Lnum++;
                String LocNum = String.format("%05d", Lnum);
                tempNode.setTextContent(LocNum);
            } else if (tempNode.getNodeName().equals("m1:PORTEDNBR")) {
            	 int TNcounter = appendNodesIterator1 + 2
                String tn = "\${TN" + TNcounter.toString() + "}";
                tempNode.setTextContent(tn);
            }
        }
        nodeList1.item(0).appendChild(nodeNew);
        appendNodesIterator1++;
    }
	return fnFormatXmlDocument(xmlDocument);

}
