String charterRepeatNodes(Document xmlDocument, int size) throws TransformerException {

    NodeList nodeList1 = xmlDocument.getElementsByTagName("apsTT:order");
    NodeList lsr = xmlDocument.getElementsByTagName("telephoneNumber");
    NodeList refNode =  xmlDocument.getElementsByTagName("apsBT:clientContact");
    Node originalNode = null;
    Node refNodes =null;

    def npa = context.expand('${#TestCase#NPA}')
    def nxx = context.expand('${#TestCase#NXX}')
    def lineNum = context.expand('${#TestCase#LineNumber}')
    //def TN = npa + nxx + lineNum

    for (int j = 0; j < lsr.getLength(); j++) {
        Node nTemp = lsr.item(j);
        if (nTemp.getNodeName().equals("telephoneNumber")) {
            originalNode = nTemp.cloneNode(true);
            break;
        }
    }
    for(int j=0; j< refNode.getLength();j++){
         Node nTemp = refNode.item(j);
         if(nTemp.getNodeName().equals("apsBT:clientContact")){
            refNodes = nTemp.cloneNode(true);
            break;
         }
  }

    int appendNodesIterator1 = 0;
    int nfromTN = 0;
    Node attrNode = null;
    Node childNode = null;    
    while (appendNodesIterator1 < size) {
         Node nodeNew = originalNode.cloneNode(true); 
         childNode = refNodes.cloneNode(true);   
         nodeList1.item(0).appendChild(nodeNew);         
         appendNodesIterator1++;
	}     
    nodeList1.item(0).appendChild(childNode);
    Element element = (Element) xmlDocument.getElementsByTagName("apsBT:clientContact").item(0);
    Node parent = element.getParentNode();
    parent.removeChild(element);
    NodeList list = xmlDocument.getElementsByTagName("apsBT:telephoneNumberInformation")
    for (int j = 0; j < list.getLength(); j++) {       
        attrNode = list.item(j);       
        String szValue = fnReadAttributes(attrNode);
        String[] szData = szValue.split(">")
        for (int i = 0; i < szData.length; i++) {
            String[] szNew = szData[i].split("@");
            if (szNew[0].equals("lineNumber")) {
                if (nfromTN == 0)
                   	nfromTN = Integer.valueOf(lineNum)
                	if(j>0)
                		nfromTN++;
                String lineNumber = String.format("%04d", nfromTN);
                szNew[1] = lineNumber+"";
            }
            ((Element) attrNode).setAttribute(szNew[0], szNew[1]);
        }
    }


    return fnFormatXmlDocument(xmlDocument);

}
String fnFormatXmlDocument(Document xmlDocument) throws TransformerException {
    TransformerFactory transformerFactory = TransformerFactory.newInstance();
    Transformer transformer = transformerFactory.newTransformer();
    transformer.setOutputProperty(OutputKeys.INDENT, "yes");
    transformer.setOutputProperty("{http://xml.apache.org/xslt}indent-amount", "2");
    StringWriter writer = new StringWriter();
    DOMSource source = new DOMSource(xmlDocument);
    StreamResult result = new StreamResult(writer);
    transformer.transform(source, result);
    String output = writer.getBuffer().toString();
    return output;
}

