            responseVerifyStatus = context.expand('${#TestCase#ResponseVerifyStatus}')
            if (responseVerifyStatus.equalsIgnoreCase("true")) {
                adapterFlag = true
                break;
            }

            supFlag = false
        }
        sup = context.expand('${#TestCase#SUP}')
        if (BpelDBEventValidationArray[dbValIterator].contains("soa_disc_pending") && customer.equalsIgnoreCase("BHN") && OrderType.equalsIgnoreCase("VOIPPORT") && OrderSubType.equalsIgnoreCase("DISCONNECT")) {
            tc.setPropertyValue("SOAResponsetype", "SOA_DISC_SUCCESSFUL")
            tcBus.testSteps["SOA Response"].run(testRunner, context)
            testRunner.runTestStepByName("Submit Response - MOCK")
            tc.setPropertyValue("InterfaceStepName", "Submit Response - MOCK")
            tcBus.testSteps["Response Verification"].run(testRunner, context)
            responseVerifyStatus = context.expand('${#TestCase#ResponseVerifyStatus}')
            if (responseVerifyStatus.equalsIgnoreCase("true")) {
                break;
            }
        }

        if ((OrderSubType.equalsIgnoreCase("PORTIN") && BpelDBEventValidationArray[dbValIterator].contains("soa_pending") && !BpelDBEventValidationArray[dbValIterator + 1].contains("supplement")) || (OrderSubType.equalsIgnoreCase("PORTIN") && BpelDBEventValidationArray[dbValIterator].contains("soa_modify_successful")) || (OrderSubType.equalsIgnoreCase("PORTIN") && BpelDBEventValidationArray[dbValIterator].contains("soa_modify_submitted")) || (OrderSubType.equalsIgnoreCase("PORT OUT") && BpelDBEventValidationArray[dbValIterator].contains("soa_rel_rejected"))) {
            if (dbValFlag.equalsIgnoreCase("true")) {
                def npqty = context.expand('${#TestCase#NPQTY}')
                def tempTN = context.expand('${#TestCase#TN}')
                for (int l = 1; l <= Integer.parseInt(npqty); l++) {
                    if (l > 1) {
                        def tn = context.expand('${#TestCase#TN}')
                        def tn1 = Integer.parseInt(tn.split("-")[2])
                        tn1 = tn1 + 1
                        tn = tn.split("-")[0] + "-" + tn.split("-")[1] + "-" + tn1.toString()
                        tc.setPropertyValue("TN", tn.toString())
                        tc.setPropertyValue("SOAIterator", l.toString())
                        tc.setPropertyValue("lineNumber", tn1.toString())
                    }
                   else{
                   		def NPA = context.expand('${#TestCase#NPA}')
                   		def NXX = context.expand('${#TestCase#NXX}')
                   		def lineNumber = context.expand('${#TestCase#lineNumber}')
                   		def tn = NPA+"-"+NXX+"-"+lineNumber
                   		tc.setPropertyValue("TN", tn.toString())


                   	}
                    tc.setPropertyValue("SOAResponsetype", "SOA_RELEASE")
                    tcBus.testSteps["SOA Response"].run(testRunner, context)
                     if(customer.equalsIgnoreCase("MPCS") || customer.equalsIgnoreCase("LYPR")) 
                     {
		                testRunner.runTestStepByName("Submit Response - WLS MOCK")
		                tc.setPropertyValue("InterfaceStepName", "Submit Response - WLS MOCK")
            		} 
            		else {
		                testRunner.runTestStepByName("Submit Response - MOCK")  
		                tc.setPropertyValue("InterfaceStepName", "Submit Response - MOCK")
            			}
                    //testRunner.runTestStepByName("Submit Response - MOCK")
                    //tc.setPropertyValue("InterfaceStepName", "Submit Response - MOCK")
                    tcBus.testSteps["Response Verification"].run(testRunner, context)
                    responseVerifyStatus = context.expand('${#TestCase#ResponseVerifyStatus}')
                    if (responseVerifyStatus.equalsIgnoreCase("true")) {
                        break;
                    }
                }
                dbValIterator++
                tc.setPropertyValue("SEAPEPDBVALCOUNTER", dbValIterator.toString())
                tc.setPropertyValue("ExpectedDBEventValues", BpelDBEventValidationArray[dbValIterator])
                tcBus.testSteps["Sea Pep DB Validation"].run(testRunner, context)
                def NPA = context.expand('${#TestCase#NPA}')
                def NXX = context.expand('${#TestCase#NXX}')
                def lineNumber = context.expand('${#TestCase#lineNumber}')
                def tn = NPA+"-"+NXX+"-"+lineNumber
                tc.setPropertyValue("TN", tn.toString())
                //tc.setPropertyValue("TN", tempTN)
                if (dbValFlag.equalsIgnoreCase("False")) {
                    break
                }
                tc.setPropertyValue("SOAResponsetype", "SOA_COMPLETE")
                tc.setPropertyValue("SOAIterator", "")
                tcBus.testSteps["SOA Response"].run(testRunner, context)
                testRunner.runTestStepByName("Submit Response - MOCK")
                tc.setPropertyValue("InterfaceStepName", "Submit Response - MOCK")
                tcBus.testSteps["Response Verification"].run(testRunner, context)
                responseVerifyStatus = context.expand('${#TestCase#ResponseVerifyStatus}')
                if (responseVerifyStatus.equalsIgnoreCase("true")) {
                    break;
                }

                dbValIterator++
                tc.setPropertyValue("SEAPEPDBVALCOUNTER", dbValIterator.toString())
                tc.setPropertyValue("ExpectedDBEventValues", BpelDBEventValidationArray[dbValIterator])
                tcBus.testSteps["Sea Pep DB Validation"].run(testRunner, context)
                for (int j = 0; j < adapterDBVal.size(); j++) {
                    adpEventCode = adapterDBVal[j].split("\\|")
                    if (adpEventCode.size() == 2) {
                        if (BpelDBEventValidationArray[dbValIterator].contains("supplement") && BpelDBEventValidationArray[dbValIterator - 1].contains(adpEventCode[0])) {
                            eventCodeToValidate = adpEventCode[0] + "|supplement"
                            flag = true
                            //                                   adapterDBVal[j] = "#"
                        }
                    } else if (adpEventCode.size() == 1) {
                        if (BpelDBEventValidationArray[dbValIterator].contains(adpEventCode[0])) {
                            eventCodeToValidate = adpEventCode[0]
                            flag = true
                            //                                   adapterDBVal[j] = "#"
                        }
                    }
                    if (flag && (eventCodeToValidate.contains("soa_complete"))) {
                        scenario = getScenrioName(adp, eventCodeToValidate, supVal)
                        tc.setPropertyValue("ScenarioName", scenario)
                        tcBus.testSteps["Adapter DB Validation"].run(testRunner, context)
                        eventCodeToValidate = ""
                        flag = false
                    }
                }
            } else {
                break;
            }
        }
        performValidFlag = false
        if (dbValIterator < BpelDBEventValidationArray.size() - 1) {
            if (BpelDBEventValidationArray[dbValIterator + 1].contains("install_complete") && !(customer.equalsIgnoreCase("MCC") && OrderType.equalsIgnoreCase("VOIPPORT") && OrderSubType.equalsIgnoreCase("PORTIN")) && !OrderSubType.equalsIgnoreCase("Reclaim") && !((OrderSubType.equalsIgnoreCase("PORT OUT")) && OrderType.equalsIgnoreCase("VOIPPORT") && customer.equalsIgnoreCase("MCC")) && !(customer.equalsIgnoreCase("NTDM"))) {
                performValidFlag = true
            }
            if (performValidFlag && sup != "1") {
                if (customer.equalsIgnoreCase("MCC") && OrderType.equalsIgnoreCase("VOIPPORT") && (OrderSubType.equalsIgnoreCase("DISCONNECT") || OrderSubType.equalsIgnoreCase("ACCOUNT CHANGE") || OrderSubType.equalsIgnoreCase("TRANSFER"))) {
                    tcBus.testSteps["AQ Admin DB"].run(testRunner, context)
                } else {
                    tcBus.testSteps["Install Complete"].run(testRunner, context)
                    count = 1
                    c = false
                    while (!c && count < 4) {
                        def b = testRunner.testCase.getTestStepByName(interfaceStepName).run(testRunner, context)
                        def a = b.status
                        c = a.toString().equals("OK")
                        count = count + 1
                    }
                    tc.setPropertyValue("InterfaceStepName", interfaceStepName)
                    tcBus.testSteps["Response Verification"].run(testRunner, context)
                    responseVerifyStatus = context.expand('${#TestCase#ResponseVerifyStatus}')
                    if (responseVerifyStatus.equalsIgnoreCase("true")) {
                        break;
                    }
                }
            }
        }

 		 if((adp.equalsIgnoreCase("GENERIC WIRELESS") || adp.equalsIgnoreCase("METRO PCS")) && (dbValIterator < BpelDBEventValidationArray.size()-1)){
 		if(BpelDBEventValidationArray[dbValIterator+1].contains("icp_successful")|| BpelDBEventValidationArray[dbValIterator+1].contains("soa_cancel_submitted")) {
	           	tcBus.testSteps["ICP Valid"].run(testRunner, context)
				count = 1
				c=false
				while(!c && count <4) {
					def b = testRunner.testCase.getTestStepByName(interfaceStepName).run(testRunner, context)
					def a= b.status
					c = a.toString().equals("OK")
					count=count+1
				}
				tc.setPropertyValue("InterfaceStepName", interfaceStepName)
				tcBus.testSteps["Response Verification"].run(testRunner, context)
           	}
  		}

 		//}
    }

    XMLValidator = context.expand('${#TestCase#XMLValidationInDB}')
    if (!XMLValidator.equals("")) {
        tcBus.testSteps["Logged XML DB Validation"].run(testRunner, context)
    }
    /*def specificValidation = context.expand('${#TestCase#SpecificValidation}')
        if (specificValidation.contains("end")) {
            tcBus.testSteps["Specific Validation"].run(testRunner, context)
        }*/
    tc.removeProperty("SUP")
    tc.removeProperty("VER")
    tc.removeProperty("ScenrioName")
    tc.removeProperty("ESRfullfillmentflag")
    tc.removeProperty("adapterDBCounter")
    tc.removeProperty("RequestXml")
}
catch (Exception e) {
    log.error '[Execution Driver]: Unable to execute driver script, exception occured:- ' + e.getMessage()
    def fileName1 = context.expand('${#TestCase#FileName}')
    def inputFile1 = new File(fileName1)
    inputFile1.append(("\r\n" + new java.util.Date()).toString() + ":ERROR:[Execution Driver]: Unable to execute driver script, exception occured:- " + e.getMessage())
}
public String getScenrioName(String adp, String eventCodeToValidate, String Sup) {
    def orderSubType = context.expand('${#TestCase#OrderSubType}')
     def Order_Type = context.expand('${#TestCase#Order_Type}')
    String scenarioName
    def sup = ""
    def order = ""
    def order_type = ""
    if (eventCodeToValidate.contains("supplement")) {
        sup = "|" + Sup
    }
    if(adp.equalsIgnoreCase("CHARTER") || adp.equalsIgnoreCase("SDL") || adp.equalsIgnoreCase("UNIVERSAL LRN CHANGE")) {
        order = "|" + orderSubType
    }
     if (adp.equalsIgnoreCase("COMCAST DL")) {
        order_type = "|" + Order_Type
    }
    scenarioName = adp + "|" + eventCodeToValidate.split("\\|")[0] + sup + order + order_type
    return scenarioName
}

