      dbConnection = context.DBConnection
      def updatedSpecificValidation = ""
      def DataBase
      ResultSet resultSet
      def specificValidation = context.expand('${#TestCase#SpecificValidation}').split("\\|\\|")
      for (int i = 0; i < specificValidation.size(); i++) {
       def DBQueryspecificValidationDetails = specificValidation[i].split("\\|")
       DataBase = DBQueryspecificValidationDetails[0]
       switch(DataBase){
         case "SEA_PEP_DB":
                def SEAPEPdbPassword = password.decryptPassword(SEAPEPdbKey, SEAPEPdbEncryptedKey)
                dbConnection.setDBProperties(SEAPEPdbDriver, SEAPEPdbUrl, SEAPEPdbUser, SEAPEPdbPassword)
                con = dbConnection.getDBConnection()
                break;
         case "ADAPTER_DB":
                def dbPassword = password.decryptPassword(dbKey, dbEncryptedKey)
                dbConnection.setDBProperties(dbDriver, dbUrl, dbUser, dbPassword)
                con = dbConnection.getDBConnection()
                break;
         case "ESR_DB":
                def ESRdbPassword = password.decryptPassword(ESRdbKey, ESRdbEncryptedKey)
                dbConnection.setDBProperties(ESRdbDriver, ESRdbUrl, ESRdbUser, ESRdbPassword)
                con = dbConnection.getDBConnection()
                break;
       }
          def twoEventCodes = DBQueryspecificValidationDetails[1].split("\\&")

          boolean executeQuery = false
          if(twoEventCodes.size() == 2 ){
          	if(eventCodes.contains(twoEventCodes[0]) && eventCodes.contains(twoEventCodes[1])){
          		executeQuery = true
          	}

          	else
          		executeQuery = false
          }
          else if(eventCodes.contains(DBQueryspecificValidationDetails[1]) || (DBQueryspecificValidationDetails[1].equals("start"))||(DBQueryspecificValidationDetails[1].equals("end"))){
          	executeQuery = true
          }
          if(executeQuery){
		  boolean flag
	       dbQuery = DBQueryspecificValidationDetails[2].split("\\[")[0]
		  def ExpectedValue = DBQueryspecificValidationDetails[2].split("\\[")[1].split("\\]")[0];             
	       tn = npa + nxx + lineNumber
	       if(dbQuery.contains("hyphen")){
	             tn= npa + "-" +nxx + "-" + lineNumber
	             testRunner.testCase.setPropertyValue("TNwithhyphen",tn) 
	       }
	        Matcher matcher = Pattern.compile("\\\$(\\w+)").matcher(dbQuery); 
	        while (matcher.find()) {
	              String actualStr = matcher.group(1);
	              dbQuery = dbQuery.replace("\$"+actualStr,"'"+testRunner.testCase.getPropertyValue(actualStr)+"'");
	        }
	      def ExpectedColumnValues=[]
           ExpectedColumnValues = ExpectedValue.split(",")
           for(int a=0;a < ExpectedColumnValues.size();a++){
           	if(ExpectedColumnValues[a].contains("\$")){
           		ExpectedColumnValues[a]=ExpectedColumnValues[a].replace("\$","")
           		ExpectedColumnValues[a]= testRunner.testCase.getPropertyValue(ExpectedColumnValues[a])
           	}
           }

	       log.info '[SPECIFIC VALIDATION]: Query to be executed in : ' + DataBase + ' and Query is : ' + dbQuery
	       inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]: Query to be executed in : " + DataBase + " and Query is : " + dbQuery)
	       Thread.sleep(30000);
	       resultSet = dbConnection.getResultSet(con,dbQuery)
	       colSize = 0

              while (resultSet.next()){
              	flag=true
	              rsmd = resultSet.getMetaData()
	              colSize=rsmd.getColumnCount()
	              for(int col=0;col<colSize;col++)
	                 {
	                   ActualColumnData[col] = resultSet.getString(col+1)          
	                   name[col] = rsmd.getColumnName(col+1)                                                          
	                   tc.setPropertyValue(name[col],ActualColumnData[col])

                 }
                 }

boolean valFlag;
List<String> failedValues = new ArrayList<String>()
if (ExpectedColumnValues.size() != 0 && ActualColumnData.size() != 0 && !ExpectedColumnValues.contains("Exist") && !ExpectedColumnValues.contains("NotExist")){		
			for (iterator = 0; iterator < ExpectedColumnValues.size(); iterator++){
				valFlag = false

				if (ActualColumnData[iterator].equalsIgnoreCase(ExpectedColumnValues[iterator])){
					log.info '[SPECIFIC VALIDATION]: DB Result: Column Name: ['+ name[iterator] +']  & Expected Value: ['+ExpectedColumnValues[iterator] + ']'
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]:DB Result: Column Name: [ " + name[iterator] +"]  & Expected Value: ["+ExpectedColumnValues[iterator]+ "]")
					log.info '[SPECIFIC VALIDATION]: DB Result: Column Name: ['+ name[iterator] +']  & Actual Value: ['+ActualColumnData[iterator] + ']'
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]:DB Result: Column Name: [ " + name[iterator] +"]  & Actual Value: ["+ActualColumnData[iterator] + "]")
					valFlag = true;
					failedValues.add("Pass");

				}
			else{
					log.error '[SPECIFIC VALIDATION]: DB Result: Column Name: ['+ name[iterator] +']  & Expected Value: ['+ExpectedColumnValues[iterator]+ ']'
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]:DB Result: Column Name: [ " + name[iterator] +"]  & Expected Value: ["+ExpectedColumnValues[iterator]+ "]")
					log.error '[SPECIFIC VALIDATION]: DB Result: Column Name: ['+ name[iterator] +']  & Actual Value: ['+ActualColumnData[iterator] + ']'
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]:DB Result: Column Name: [ " + name[iterator] +"]  & Actual Value: ["+ActualColumnData[iterator] + "]")
					failedValues.add("Fail");

				}
				if(!valFlag){
					log.error '[SPECIFIC VALIDATION]: Expected Value is not present in DB'
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]: Expected Value is not present in DB")
				//failedValues.add(actualValue)

				}
			}



		}

              if (ExpectedValue.equalsIgnoreCase("Exist") && flag) {
              		log.info '[SPECIFIC VALIDATION]: In : ' + DataBase + ' Query Executed is : '+ dbQuery + ' Result row is present in DB as expected'
              	     inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]: In : " + DataBase + ' Query Executed is : '+ dbQuery + ' Result row is present in DB as expected')
              	     flag=true
              }
              else if (ExpectedValue.equalsIgnoreCase("NotExist") && !flag) {
              		log.info '[SPECIFIC VALIDATION]: In : ' + DataBase + ' Query Executed is : '+ dbQuery + ' There is not result present in DB as expected'
              		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]: In : " + DataBase + ' Query Executed is : '+ dbQuery + ' There is not result present in DB as expected')
              	     flag= true
              }

            	if(!flag){
	            	 log.info '[SPECIFIC VALIDATION]: Query Executed Successfully in : ' + DataBase + ' No Result found in DB'
	            	 inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]: Query Executed Successfully in : " + DataBase + " No Result found in DB")	     
	         	}
	         specificValidation[i]=DBQueryspecificValidationDetails[0]+"|#|"+DBQueryspecificValidationDetails[2]
		     if(!flag){
				status = "FAIL"
				log.error '[SPECIFIC VALIDATION]: ' + status	
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[SPECIFIC VALIDATION]: " + status)
				tc.setPropertyValue("preValFlag",flag.toString())
			}
			else if(failedValues.contains("Fail")){
				status = "FAIL"
				log.error '[SPECIFIC VALIDATION]: ' + status	
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[SPECIFIC VALIDATION]: " + status)
				tc.setPropertyValue("preValFlag",valFlag.toString())
			}
			else{
				status = "PASS"
				log.info '[SPECIFIC VALIDATION]: ' + status	
				inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[SPECIFIC VALIDATION]: " + status)	
				tc.setPropertyValue("preValFlag",flag.toString())
			}

	      }
      updatedSpecificValidation = updatedSpecificValidation + specificValidation[i] +"||"

   }
   updatedSpecificValidation=updatedSpecificValidation.substring(0, updatedSpecificValidation.length()-2);
   testRunner.testCase.setPropertyValue("specificValidation",updatedSpecificValidation)

}catch(Exception e){
	 log.error "[SPECIFIC VALIDATION]: Unable to perform DB Validation - " + e.getMessage()
	 inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[SPECIFIC VALIDATION]: Unable to perform DB Validation - " + e.getMessage())
}finally {
	con.close()
}

