	totaltime = 0
	timeOut = Integer.parseInt(context.expand('${#Project#DBExplicitTimeOut}'))
	//Decrypting password
	tcGen.testSteps["Password Decryption"].run(testRunner, context)
	def password = context.PasswordDecryption
	def dbPassword = password.decryptPassword(dbKey,dbEncryptedKey)
	tcGen.testSteps["DB Connection"].run(testRunner, context)
	try {
		dbConnection = context.DBConnection
		dbConnection.setDBProperties(dbDriver,dbUrl,dbUser,dbPassword)
		con = dbConnection.getDBConnection()
		// Fetching Correlation ID 
		dbQuery = "Select CORRELATION_ID from ESR_ORDER_MESSAGE where ORDERID='" + context.expand('${#TestCase#ORDERID}')  + "' and EVENT='icp_confirmed' and VER = '" + context.expand('${#TestCase#VER}') + "'"
		while(CorrelationID == null) {
			resultSet = dbConnection.getResultSet(con,dbQuery)
			while (resultSet.next()) {
				CorrelationID = resultSet.getString("CORRELATION_ID")		
			}
			//Explicit Delay to fetch Correlation ID
			tcGen.testSteps["Delay"].run(testRunner, context)
			totaltime = totaltime + 3000
			if (totaltime >= timeOut) {
				break;
			}
		}
	} catch (Exception e) {
		log.error "[ICP VALID REQUEST ENVELOPE]:Unable to Fetech Correlation ID :-" + e.getMessage()
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[ICP VALID REQUEST ENVELOPE]:Unable to Fetech Correlation ID :-" + e.getMessage())
	}
	log.info "[ICP VALID REQUEST ENVELOPE]:Query Executed to Fetech Correlation ID :-" + dbQuery
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[ICP VALID REQUEST ENVELOPE]:Query Executed to Fetech Correlation ID :-" + dbQuery)
	log.info "[ICP VALID REQUEST ENVELOPE]:Correlation ID is :- " + CorrelationID
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[ICP VALID REQUEST ENVELOPE]:Correlation ID is :- " + CorrelationID)
	tc.setPropertyValue("CORRELATIONID",CorrelationID)
	def test_Suite = product + "_REQUEST_TEMPLATES"
	def test_Case = adapter + "_REQUEST_TEMPLATES"
	def test_Step = "ICP_Valid"
	log.info '[ICP VALID REQUEST ENVELOPE]: TEST CASE:-' + tc_Name
	log.info '[ICP VALID REQUEST ENVELOPE]: TEMPLATE NAME:-' + test_Step
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[ICP VALID REQUEST ENVELOPE]: TEST CASE:- " + tc_Name)
     inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[ICP VALID REQUEST ENVELOPE]: TEMPLATE NAME:- " + test_Step)

     req_XML = project.getTestSuiteByName(test_Suite).getTestCaseByName(test_Case).getTestStepByName(test_Step).getPropertyValue("Request")
	//Find parameteres using Regular Expression
	Pattern regex = Pattern.compile("(>\\\$((.*))<)")
	Matcher matcher = regex.matcher(req_XML)
	def prop_Key = []
	def j = 0;
	def prop_Value = []
	req_XML.replaceAll(">\r",">")
	log.info '[ICP VALID REQUEST ENVELOPE]: NODES TO REPLACE IN REQUEST ENVELOPE-'
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[ICP VALID REQUEST ENVELOPE]: NODES TO REPLACE IN REQUEST ENVELOPE-")
	// Declare Hashmap to store Test Data
	Map<String,String> nodes_ToReplace = new HashMap<String,String>()
	while(matcher.find()){
		for (int i = 0; i < matcher.groupCount(); i++) 
		{

				if(i == 1||i==3)
		       {
					nodes_ToReplace.put(matcher.group(1).substring(1, matcher.group(1).length() - 1) , matcher.group(3).substring(1, matcher.group(3).length() - 1))
		       }

		}
	}

	Set<String> keys = nodes_ToReplace.keySet()

	InputStream XmlParse  = new ByteArrayInputStream(req_XML.getBytes("UTF-8"));
	DocumentBuilderFactory docBuilderFactory = DocumentBuilderFactory.newInstance()
	DocumentBuilder docBuilder = docBuilderFactory.newDocumentBuilder()
	Document document = docBuilder.parse(XmlParse)
	NodeList nodeList = document.getElementsByTagName("*")
	def node_Names = []
	def iterator = 1

	def tagName
      def tcPropCount = tc.getPropertyCount()
      String[] tcPropName = new String[tcPropCount]
      for(int i = 0; i<tcPropCount;i++) {
       tcPropName[i] = tc.getPropertyAt(i).name
      }

      for (int etemp = 0; etemp < nodeList.getLength(); etemp++) {
       Node eNode = nodeList.item(etemp)
       for (int k=0; k<tcPropCount; k++) {
        if(eNode.getNodeName().contains(":")){
         tagName = eNode.getNodeName().split(":")[1]                
        } 
        else {
         tagName = eNode.getNodeName()
        }
        if(tagName.equalsIgnoreCase(tcPropName[k]) && !eNode.getTextContent().contains("\${") && !tc.getPropertyValue(tcPropName[k]).equals("")) {
         eNode.setTextContent(tc.getPropertyValue(tcPropName[k]))
         log.info "[ICP VALID REQUEST ENVELOPE]: " + iterator + ". " + eNode.getNodeName() + " = " + tc.getPropertyValue(tcPropName[k])
         inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[ICP VALID REQUEST ENVELOPE]: " + iterator + ". " + eNode.getNodeName() + " = " + tc.getPropertyValue(tcPropName[k]))                
         iterator ++
        }              
       }

       if (eNode.getNodeType() == Node.ELEMENT_NODE) {
        node_Names [etemp] = eNode.getNodeName()
        for(String key: keys){
            if(key.equalsIgnoreCase(eNode.getTextContent())){
               log.info "[ICP VALID REQUEST ENVELOPE]: " + iterator + ". " + nodes_ToReplace.get(key) + " = " + testRunner.testCase.getPropertyValue(nodes_ToReplace.get(key))
               inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[ICP VALID REQUEST ENVELOPE]: " + iterator + ". " + nodes_ToReplace.get(key) + " = " + testRunner.testCase.getPropertyValue(nodes_ToReplace.get(key)))
               eNode.setTextContent(tc.getPropertyValue(nodes_ToReplace.get(key)))
               iterator ++
            }
        }
       }
       for (j = 0; j < nodeList.item(etemp).getAttributes().getLength();j++) {
        if (nodeList.item(etemp).getAttributes().getLength() > 0 && nodeList.item(etemp).getAttributes().item(j).getNodeValue().startsWith("\${")){
         Node value = nodeList.item(etemp).getAttributes().item(j);
         def attrValue = value.getNodeValue();
         def valueToReplace = attrValue.substring(2, attrValue.length() - 1);
         log.info "[ICP VALID REQUEST ENVELOPE]: " + iterator + ". " + valueToReplace + " = " + testRunner.testCase.getPropertyValue(valueToReplace)
         inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[ICP VALID REQUEST ENVELOPE]: " + iterator + ". " + valueToReplace + " = " + testRunner.testCase.getPropertyValue(valueToReplace))
         value.setNodeValue(tc.getPropertyValue(valueToReplace));
         iterator ++
        }
       }
 }

	TransformerFactory transformerFactory = TransformerFactory.newInstance()
	Transformer transformer = transformerFactory.newTransformer()
	StringWriter writer = new StringWriter()
	DOMSource source = new DOMSource(document)
	StreamResult result = new StreamResult(writer)
	transformer.transform(source, result)
	def output = writer.getBuffer().toString()
	output = output.replaceAll('xmlns=""', "") //remove blank xmlns declaration 
	tc.setPropertyValue("RequestXml",output)
	log.info '[ICP VALID REQUEST ENVELOPE]:- Final Payload:-' +output
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[ICP VALID REQUEST ENVELOPE]:- Final Payload:-" +output)
	testRunner.testCase.setPropertyValue("TestStepToBeVerify","ICP VALID")
}catch(Exception e){
	log.error "[ICP VALID REQUEST ENVELOPE]: Unable to generate request XML - " + e.getMessage()
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[ICP VALID REQUEST ENVELOPE]: Unable to generate request XML - " + e.getMessage())
	testRunner.testCase.setPropertyValue("TestStepToBeVerify","ICP VALID")
} finally {
	con.close()	
}

