def groovyUtils = new  com.eviware.soapui.support.GroovyUtils(context)
def ProjectPath = groovyUtils.projectPath
def adapter = context.expand('${#TestCase#Adapter}')
def pon = context.expand('${#TestCase#PON}')
String replaceTag = context.expand('${#TestCase#ReplaceTagValueInXml}')
String replaceTagValueInXml=null;
if(adapter.equalsIgnoreCase("UNIVERSAL LRN CHANGE") && replaceTag.contains("ns2:NewSPDueDate") || replaceTag.contains("ns2:CustomerDisconnectDate") || replaceTag.contains("ns2:EffectiveReleaseDate")){
 replaceTagValueInXml= context.expand('${#TestCase#ReplaceTagValueInXml}').split("@")[0]
}
else{
 replaceTagValueInXml= context.expand('${#TestCase#ReplaceTagValueInXml}').split("-")[0]
}
try{

	    def req_XML = context.expand('${#TestCase#Request}')  
	    InputStream XmlParse  = new ByteArrayInputStream(req_XML.getBytes("UTF-8"));
	    DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
	    DocumentBuilder builder = factory.newDocumentBuilder();
	    Document xmlDocument = builder.parse(XmlParse);
	    if(adapter.equalsIgnoreCase("GENERIC ASR")){
	    	String[] tagName = replaceTagValueInXml.split("\\|\\|");
        	for(String value:tagName){          
        		String[] tagValue = value.split("\\|");
        		for(int i=0;i<tagValue.length;i++){
        			NodeList Len = xmlDocument.getElementsByTagName(tagValue[i]);
        			for(int j=0;j<Len.getLength();j++){
					NamedNodeMap attr = (Len.item(j)).getAttributes();
					Node nodeAttr = attr.getNamedItem("value");
					nodeAttr.setTextContent(tagValue[i+1]);
        			}
        		}
        	}
	    }
	    else{
	    NodeList nodeList = xmlDocument.getElementsByTagName("*");
	    String[] specValue = replaceTagValueInXml.split("\\|\\|");	
		for (int etemp = 0; etemp < nodeList.getLength(); etemp++) {
			Node eNode = nodeList.item(etemp);

		for(String value: specValue){	
				String[] tagName = value.split("\\|");				
				for(int i=0;i<tagName.length;i++){
					if(eNode.getNodeName().equalsIgnoreCase(tagName[i])){
						if(tagName[i+1].contains("CNAM"))
						{
							tagName[i+1]=tagName[i+1]+pon
 							tagName[i+1]=tagName[i+1].substring(0,15)
							}
						eNode.setTextContent(tagName[i+1]);
						log.info tagName[i+1]
					}
				}


			}
		}			    
	    }
	    TransformerFactory transformerFactory = TransformerFactory.newInstance();
	    Transformer transformer = transformerFactory.newTransformer();
	    transformer.setOutputProperty(OutputKeys.INDENT, "yes");
	    transformer.setOutputProperty("{http://xml.apache.org/xslt}indent-amount", "2");
	    StringWriter writer = new StringWriter();
	    DOMSource source = new DOMSource(xmlDocument);
	    StreamResult result = new StreamResult(writer);
	    transformer.transform(source, result);
	    String output = writer.getBuffer().toString();  

	      if (adapter.equalsIgnoreCase("GENERIC WIRELESS")||adapter.equalsIgnoreCase("COMMERCIAL")||adapter.equalsIgnoreCase("GENERIC WIRELINE")||adapter.equalsIgnoreCase("COMCAST DL")||adapter.equalsIgnoreCase("METRO PCS")||adapter.equalsIgnoreCase("AN")){
			output = output.replaceAll('xmlns=""', "") //remove blank xmlns declaration
}           
	    testRunner.testCase.setPropertyValue("Request",output);           

}
catch(Exception e){

}

