String FormatXmlAttributeValue(String reqXML, String testStep, File inputFile)
{
	tc = testRunner.testCase;
	//Format param Values in Template
	InputStream XmlParse  = new ByteArrayInputStream(reqXML.getBytes("UTF-8"));
	DocumentBuilderFactory docBuilderFactory = DocumentBuilderFactory.newInstance()
	DocumentBuilder docBuilder = docBuilderFactory.newDocumentBuilder()
	Document document = docBuilder.parse(XmlParse)

	xpath = XPathFactory.newInstance().newXPath()
	NodeList nodes = (NodeList)xpath.evaluate("//*[contains(@value, '\$')]",document, XPathConstants.NODESET)

	log.info '[REQUEST ENVELOPE]: NODES TO REPLACE IN REQUEST ENVELOPE-:-'
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[REQUEST ENVELOPE]: NODES TO REPLACE IN REQUEST ENVELOPE-:-")
	for(int idx = 0; idx < nodes.getLength();idx++){
		Node value = nodes.item(idx).getAttributes().getNamedItem("value")
		String attrValue = value.getNodeValue()
		String finalValue = tc.getPropertyValue(attrValue.substring(2,attrValue.length()-1))
		log.info '[REQUEST ENVELOPE]: ' + idx + ". " +   attrValue.substring(2,attrValue.length()-1) + " = " + finalValue
		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[REQUEST ENVELOPE]: " + idx + ". " + attrValue.substring(2,attrValue.length()-1) + " = " + finalValue)
		value.setNodeValue(finalValue)
	}

	TransformerFactory transformerFactory = TransformerFactory.newInstance()
	Transformer transformer = transformerFactory.newTransformer()
	StringWriter writer = new StringWriter()
	DOMSource source = new DOMSource(document)
	StreamResult result = new StreamResult(writer)
	transformer.transform(source, result)
	output = writer.getBuffer().toString()
	return output
}

