//Method for converting CSV File to XML File
public void convertFile(String csvFileName, String xmlFileName, String testing, String env) {
 try {
  domFactory = DocumentBuilderFactory.newInstance();
  domBuilder = domFactory.newDocumentBuilder();
 } catch (FactoryConfigurationError exp) {
  System.err.println(exp.toString());
 } catch (ParserConfigurationException exp) {
  System.err.println(exp.toString());
 } catch (Exception exp) {
  System.err.println(exp.toString());
 }
 int rowsCount = -1;
 try {
  Document newDoc = domBuilder.newDocument();
  // Root element
  Element rootElement = newDoc.createElement("TestSuite");
  newDoc.appendChild(rootElement);

  // ** Now using the OpenCSV **//
  CSVReader reader = new CSVReader(new FileReader(csvFileName));
  String[] nextLine;
  int line = 0;
  List < String > headers = new ArrayList < String > (5);
  while ((nextLine = reader.readNext()) != null) {

   if (line == 0) { // Header row
    for (String col: nextLine) {
     headers.add(col);
    }
   } else { // Data row
    Element rowElement = newDoc.createElement("TestCase");
    int col = 0;
    for (String value: nextLine) {
     String header = headers.get(col);
     if (header.equalsIgnoreCase("Execution") && value.trim().equalsIgnoreCase("Y")) {
      if (headers.get(headers.indexOf(testing)).equalsIgnoreCase(testing) && nextLine[headers.indexOf(testing)].trim().equalsIgnoreCase("Y") && nextLine[headers.indexOf("Env")].trim().contains(env)) {
       rowElement.setAttribute("execution", value.trim());
       rootElement.appendChild(rowElement);
       int colNum = 0;
       for (String value1: nextLine) {
        String header1 = headers.get(colNum);
        if (header1.equalsIgnoreCase("TestCaseName")) {
         rowElement.setAttribute("name", value1.trim());
         rootElement.appendChild(rowElement);
        } else if (!header1.equalsIgnoreCase("TestCaseName") && !header1.equalsIgnoreCase("Execution")) {
         Element curElement = newDoc.createElement(header1);
         curElement.appendChild(newDoc.createTextNode(value1.trim()));
         rowElement.appendChild(curElement);
        }
        colNum++;
       }
      }
     }
     col++;
    }
   }
   line++;
  }
  // ** End of CSV parsing**//

  FileWriter writer = null;

  try {

   writer = new FileWriter(new File(xmlFileName));

   TransformerFactory tranFactory = TransformerFactory
    .newInstance();
   Transformer aTransformer = tranFactory.newTransformer();
   aTransformer.setOutputProperty(OutputKeys.INDENT, "yes");
   aTransformer.setOutputProperty(OutputKeys.METHOD, "xml");
   aTransformer.setOutputProperty(
    "{http://xml.apache.org/xslt}indent-amount", "4");

   Source src = new DOMSource(newDoc);
   Result result = new StreamResult(writer);
   aTransformer.transform(src, result);

   writer.flush();

  } catch (Exception exp) {
   exp.printStackTrace();
  } finally {
   try {
    writer.close();
   } catch (Exception e) {}
  }
 } catch (IOException exp) {
  log.error exp.toString();
 } catch (Exception exp) {
  log.error exp.toString();
 }

}

