        for (def q = 1; q < LineCount; q++) {
            if ((lines[q].contains(":INFO:") == true || lines[q].contains(":ERROR:") == true)) {
                if (q == 1) {
                    if (lines[q].contains(":INFO:"))
                        myDate = lines[q].split(":INFO:")[0]
