                fileReport.getTest().setEndedTime(new Date(sdf.parse(endDate).getTime()));
            }
            if ((lines[q].contains(":INFO:") == true || lines[q].contains(":ERROR:") == true)) {
                HEADER = lines[q]
                start_pos = HEADER.indexOf("[")
                data = HEADER.substring(start_pos + 1, HEADER.indexOf("]"))
                if (data.equals(aa)) {
                    if (lines[q].contains(":INFO:") == true) {

                        Result_value = "PASS"
                    } else {

                        Result_status = "FAIL"
                        Result_status1 = Result_status1 + "," + Result_status
                        if (Result_status1.contains("FAIL")) {
                            FAIL_STATUS = true
                        }
                    }
                }
            }
        }
        if (FAIL_STATUS) {
            Result_value = "FAIL"
        }
        Result_array << Result_value

    }

    def start_line, middle_line, end_line
    def array_size
    def Step_Result

    for (def f = 0; f <= Line_array.size() - 1; f++) {
        array_size = Line_array.size() - 1
        if (f == array_size) {
            start_line = Line_array[f]
            end_line = LineCount-1
        } else {
            start_line = Line_array[f]
            middle_line = Line_array[f + 1]
            end_line = middle_line - 1
        }

        file = report.startTest(CREATE_REQUEST_JSON[f], "");
        Step_Result = Result_array[f]
        file.log(LogStatus.
            "$Step_Result", CREATE_REQUEST_JSON[f].trim(), GET_TEST_STEP_DETAIL(start_line, end_line));
        fileReport.appendChild(file)
    }
    report.endTest(fileReport);
    report.flush();
    report.close();
}
    TotalPassCount=TotalCaseCount-TotalFailCount
    Project.setPropertyValue("TotalPassCount", TotalPassCount.toString());
    Project.setPropertyValue("TotalFailCount", TotalFailCount.toString());
    if(TotalFailCount>0)
    {
    	tc.setPropertyValue("REPORT_STATUS","FAIL")
    }
    else
    {
    	tc.setPropertyValue("REPORT_STATUS","PASS")
    }
    log.info "Total Count is : "+TotalCaseCount
    log.info "Fail Count is : "+ TotalFailCount
    log.info "Pass Count is : "+ TotalPassCount
}
catch(Exception e)
{
	log.error "[HTML Report]: "+ e.toString()
}
def GET_TEST_STEP_DETAIL(int a, int b) {
    String DATA_LIST = " "
    def DATA_ARRAY
    def LINE_DATA
    for (z = a; z <= b; z++) {
        LINE_DATA = lines[z]
        if (LINE_DATA != null) {
            if (LINE_DATA.contains(":INFO:") || LINE_DATA.contains(":ERROR:")) {
                DATA_ARRAY = LINE_DATA.split("]:")
                LINE_DATA = DATA_ARRAY[1] + "<br>"
            }
        }
        //log.info LINE_DATA
        DATA_LIST = DATA_LIST + LINE_DATA
        DATA_LIST = DATA_LIST.replaceAll("<", "&lt;").replaceAll(">", "&gt;")
        DATA_LIST = DATA_LIST + "<br>"
    }
    DATA_LIST = DATA_LIST.replaceAll("&lt;br&gt;", "<br>")
    return DATA_LIST
}

