String tc_Results = context.expand('${#TestCase#tcResultsCollection}')
try {

        String line = "";
        String cvsSplitBy = ",";
        int col = 0;
        String replace = "";
        BufferedReader br = new BufferedReader(new FileReader(file));
        lineReader = br.readLine();
        char[] str = lineReader.toCharArray();
        int colSize = 0;
        for (int i = 0; i < lineReader.length(); i++) {
            if (str[i] == ',') {
                ++colSize;
            }
        }
        		  String[] cellIterator = lineReader.split(cvsSplitBy);
			  StringTokenizer token = new StringTokenizer(tc_Results, "|");
			  int count = token.countTokens();
			  int tc_Index =0;
			  int row = 1;
			  for(int i=0;i<=count;i++){ 			  	
			     String testCase = token.nextToken();
			     String[] testName = testCase.split(":");
				String testCaseName = testName[0];
				String EventStatus = testName[1];



			   while ((line = br.readLine()) != null)
			   {	 	
				   if(testCaseName.equals(line.split(cvsSplitBy,-1)[0])){
				      // use comma as separator	 

				     String[] temp = line.split(cvsSplitBy,-1);
				     for(int j=1;j<=colSize;j++)
				     {
				     	if(cellIterator[j].equals("LastEventStatus")){
				     		temp[j] = EventStatus;
				     		col =j;
				     		replace = temp[j];
				     		tc_Index = row;
				     		row++;				     		
				     		break;

				     	}
				     }
				     break;

			      }
				   row++;
			  }

			   if(col !=0 && replace !=null || replace !=""){
			   updateCSV(file,replace,tc_Index,col);
			   }

			  }			  
    		}

		catch (Exception e){
		 } 

public void updateCSV(String file, String replace, int tc_Index, int col) throws IOException {

    // Read existing file 
    CSVReader reader = new CSVReader(new FileReader(file));    
    List < String[] > csvBody = reader.readAll();
    // get CSV row column  and replace with by using row and column      
    csvBody.get(tc_Index)[col] = replace;
    reader.close();

    // Write to CSV file which is open
    CSVWriter writer = new CSVWriter(new FileWriter(file));
    writer.writeAll(csvBody);
    writer.flush();
    writer.close();
}

