class DBConnection
{
	def dbDriver
	def dbConnectionUrl
	def dbUsername
	def dbPassword
	Connection con
	// Class constructor with same case as Class name
	def DBConnection()
	{
	}

	def setDBProperties(dbDriver,dbConnectionUrl,dbUsername,dbPassword)
	{
		this.dbDriver = dbDriver
		this.dbConnectionUrl = dbConnectionUrl
		this.dbUsername = dbUsername
		this.dbPassword = dbPassword
	}
	//DB Connection Setup
	Connection getDBConnection()
	{
	      // do some stuff to prove I've run with right context, etc.
	      //Class.forName(this.dbDriver);
	      con = DriverManager.getConnection(this.dbConnectionUrl,this.dbUsername,this.dbPassword)
	      return con
	}
	//Executing Query
	ResultSet getResultSet(Connection con,String query)
	{
		Statement stmt = con.createStatement();
		ResultSet resultSet = stmt.executeQuery(query);
		//log.info "return "+resultSet
		return resultSet
	}
	def closeDBConnection()
	{
		con.close()
	}

}
context.setProperty("DBConnection", new DBConnection())

import java.io.FileNotFoundException;
import java.io.IOException;
import java.security.InvalidAlgorithmParameterException;
import java.security.InvalidKeyException;
import java.security.NoSuchAlgorithmException;

import javax.crypto.BadPaddingException;
import javax.crypto.Cipher;
import javax.crypto.IllegalBlockSizeException;
import javax.crypto.KeyGenerator;
import javax.crypto.NoSuchPaddingException;
import javax.crypto.SecretKey;
import javax.crypto.spec.SecretKeySpec;

class DecryptPassword
{
	static String AES = "AES";
	String byteArrayToHexString(byte[] b) {
		StringBuffer sb = new StringBuffer(b.length * 2);
		for (int i = 0; i < b.length; i++) {
			int v = b[i] & 0xff;
			if (v < 16) {
				sb.append('0');
			}
			sb.append(Integer.toHexString(v));
		}
		return sb.toString().toUpperCase();
	}
	byte[] hexStringToByteArray(String s) {
		byte[] b = new byte[s.length() / 2];
		for (int i = 0; i < b.length; i++) {
			int index = i * 2;
			int v = Integer.parseInt(s.substring(index, index + 2), 16);
			b[i] = (byte) v;
		}
		return b;
	}
	String decryptPassword(String tempkey, String passwordkey)
			throws NoSuchAlgorithmException, NoSuchPaddingException,
			InvalidKeyException, IllegalBlockSizeException,
			BadPaddingException, FileNotFoundException, IOException {

		byte[] bytekey = hexStringToByteArray(tempkey);
		SecretKeySpec sks = new SecretKeySpec(bytekey,DecryptPassword.AES)
		Cipher cipher = Cipher.getInstance(DecryptPassword.AES)
		cipher.init(Cipher.DECRYPT_MODE, sks)
		byte[] decrypted = cipher.doFinal(hexStringToByteArray(passwordkey))
		String OriginalPassword = new String(decrypted)
		return OriginalPassword

	}
}

context.setProperty("PasswordDecryption", new DecryptPassword())

import java.io.File;
import java.util.HashMap;
import java.util.Map;
import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.xpath.XPath;
import javax.xml.xpath.XPathConstants;
import javax.xml.xpath.XPathExpression;
import javax.xml.xpath.XPathFactory;
import org.apache.log4j.Logger;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;

class InstructionXml
{
      public Object[][] parseTestFlowXml(def fxmlFile) {
            Object[][] trueTCArray
            try {
                  DocumentBuilder dBuilder = DocumentBuilderFactory.newInstance().newDocumentBuilder();
                  Document doc = dBuilder.parse(fxmlFile);
                  doc.getDocumentElement().normalize();
                  Node rootNode = doc.getDocumentElement(); // saving root node in a variable.

                  // NodeList trueTCNodes = null;
                  XPath xpath = XPathFactory.newInstance().newXPath();
                  XPathExpression expr = xpath.compile("//TestCase");
                  NodeList trueTCNodes = (NodeList) expr.evaluate(doc,XPathConstants.NODESET);

                  trueTCArray = new Object[trueTCNodes.getLength()][2];
                  for (int i = 0; i < trueTCNodes.getLength(); i++) {
                        String tcName;
                        String execution;
                        Map<String, String> prop = new HashMap<String, String>();
                        Element ele = (org.w3c.dom.Element) trueTCNodes.item(i);
                        tcName = ele.getAttribute("name");
                        execution = ele.getAttribute("execution");
                        NodeList propertiesNodes = trueTCNodes.item(i).getChildNodes();

                        boolean flag = false;
	                   for (int j = 0; j < propertiesNodes.getLength(); j++) {
                           if (!(propertiesNodes.item(j).getNodeName().equals(null))) {
                                 if (!(propertiesNodes.item(j).getNodeName().equals("#text"))) {
                                   try{
                                         prop.put(propertiesNodes.item(j).getNodeName(),
                                         propertiesNodes.item(j).getFirstChild().getNodeValue());
                                   }catch(NullPointerException e){}
                          	}
                         }
                                  }
                        prop.put("execution",execution)
                        trueTCArray[i][0] = tcName;
                        trueTCArray[i][1] = prop;
                  }
       } catch (Exception e) {
             e.printStackTrace();
       }

  		return trueTCArray;
	}
}

context.setProperty("ReadXML", new InstructionXml())

import java.io.File;
