import com.eviware.soapui.support.XmlHolder;

def groovyUtils=new  com.eviware.soapui.support.GroovyUtils(context)
def product = context.expand('${#Project#Product}')
def adapter = context.expand('${#TestCase#Adapter}')
def protocolType = context.expand('${#Project#MESSAGE_TYPE}')
def tc_Name = context.expand('${#TestCase#TestCaseName}')

def project = testRunner.testCase.testSuite.project
def tc = testRunner.testCase;
def fileName = context.expand('${#TestCase#FileName}')
def inputFile = new File(fileName)

try{
	def test_Suite = product + "_REQUEST_TEMPLATES"
	def test_Case = adapter + "_REQUEST_TEMPLATES"
	def test_Step = context.expand('${#TestCase#InputXmlType}')

	log.info '[REQUEST ENVELOPE]: TEST CASE:-' + tc_Name
	log.info '[REQUEST ENVELOPE]: TEMPLATE NAME:-' + test_Step
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[REQUEST ENVELOPE]: TEST CASE:-" + tc_Name)
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[REQUEST ENVELOPE]: TEMPLATE NAME:-" + test_Step)
	def req_XML;
	if(test_Step.contains("Commercial")){
		library = testRunner.testCase.testSuite.project.testSuites["Library"]
		module = library.testCases["Utilities"].testSteps["Repeating Nodes"]
		module.run(testRunner, context)    
	     req_XML=tc.getPropertyValue("Request"); 
	}
	else{
	     req_XML = project.getTestSuiteByName(test_Suite).getTestCaseByName(test_Case).getTestStepByName(test_Step).getPropertyValue("Request")
	}
	//Find parameteres using Regular Expression
	Pattern regex = Pattern.compile("(>\\\$((.*))<)")
	Matcher matcher = regex.matcher(req_XML)
	def prop_Key = []
	def j = 0;
	def prop_Value = []
	req_XML.replaceAll(">\r",">")
	log.info '[REQUEST ENVELOPE]: NODES TO REPLACE IN REQUEST ENVELOPE-'
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[REQUEST ENVELOPE]: NODES TO REPLACE IN REQUEST ENVELOPE-")
	// Declare Hashmap to store Test Data
	Map<String,String> nodes_ToReplace = new HashMap<String,String>()
	while(matcher.find()){
		for (int i = 0; i < matcher.groupCount(); i++) 
		{

				if(i == 1||i==3)
		       {
					nodes_ToReplace.put(matcher.group(1).substring(1, matcher.group(1).length() - 1) , matcher.group(3).substring(1, matcher.group(3).length() - 1))
		       }

		}
	}

	Set<String> keys = nodes_ToReplace.keySet()

	InputStream XmlParse  = new ByteArrayInputStream(req_XML.getBytes("UTF-8"));
	DocumentBuilderFactory docBuilderFactory = DocumentBuilderFactory.newInstance()
	DocumentBuilder docBuilder = docBuilderFactory.newDocumentBuilder()
	Document document = docBuilder.parse(XmlParse)
	NodeList nodeList = document.getElementsByTagName("*")
	def node_Names = []
	def iterator = 1

	for (int etemp = 0; etemp < nodeList.getLength(); etemp++)
	{
		Node eNode = nodeList.item(etemp);
		if (eNode.getNodeType() == Node.ELEMENT_NODE) 
		{
			node_Names [etemp] = eNode.getNodeName()
			for(String key: keys){
			    if(key.equalsIgnoreCase(eNode.getTextContent())){
			    		log.info "[REQUEST ENVELOPE]: " + iterator + ". " + nodes_ToReplace.get(key) + " = " + testRunner.testCase.getPropertyValue(nodes_ToReplace.get(key))
			    		inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[REQUEST ENVELOPE]: " + iterator + ". " + nodes_ToReplace.get(key) + " = " + testRunner.testCase.getPropertyValue(nodes_ToReplace.get(key)))
			    		eNode.setTextContent(tc.getPropertyValue(nodes_ToReplace.get(key)))
			    		iterator ++
			    }

			}
		}
		for (j = 0; j < nodeList.item(etemp).getAttributes().getLength();j++){
			if (nodeList.item(etemp).getAttributes().getLength() > 0 && nodeList.item(etemp).getAttributes().item(j).getNodeValue().startsWith("\${")){
					Node value = nodeList.item(etemp).getAttributes().item(j);
					def attrValue = value.getNodeValue();
					def valueToReplace = attrValue.substring(2, attrValue.length() - 1);
					log.info "[REQUEST ENVELOPE]: " + iterator + ". " + valueToReplace + " = " + testRunner.testCase.getPropertyValue(valueToReplace)
					inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[REQUEST ENVELOPE]: " + iterator + ". " + valueToReplace + " = " + testRunner.testCase.getPropertyValue(valueToReplace))
					value.setNodeValue(testRunner.testCase.getPropertyValue(valueToReplace));
					iterator ++
			}
		}
	}

	TransformerFactory transformerFactory = TransformerFactory.newInstance()
	Transformer transformer = transformerFactory.newTransformer()
	StringWriter writer = new StringWriter()
	DOMSource source = new DOMSource(document)
	StreamResult result = new StreamResult(writer)
	transformer.transform(source, result)
	def output = writer.getBuffer().toString()
	log.info '[REQUEST ENVELOPE]:- Final Payload:-' +output
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":INFO:[REQUEST ENVELOPE]:- Final Payload:-" +output)
}catch(Exception e){
	log.error "[REQUEST ENVELOPE]: Unable to generate request XML - " + e.getMessage()
	inputFile.append("\r\n"+(new java.util.Date()).toString()+ ":ERROR:[REQUEST ENVELOPE]:- Unable to generate request XML - " + e.getMessage())
}

